/// <summary>
/// Set and or returns the value of the PmfPriority field
/// </summary>
/// <param name="_set">Set value to field</param>
/// <param name="_reqTrans">ReqTrans record</param>
/// <param name="_current">Current value of form  PmfPriority field</param>
/// <returns>Returns current value of PmfPriority field</returns>
public edit PmfPlanPriorityCurrent pmfPriority(
boolean                 _set,
ReqTrans                _reqTrans,
PmfPlanPriorityCurrent  _current)
{
    ReqItemTable reqItemTable;
    ReqTrans     updReqTrans;

    if (hasFieldAccess(tablenum(ReqTrans),
                    fieldnum(ReqTrans,PmfPlanPriorityCurrent),
                    _set ? AccessType::Edit : AccessType::View)
    && hasFieldAccess(tablenum(ReqItemTable),
                    fieldnum(ReqItemTable,PmfPlanPriorityCurrent),
                    _set ? AccessType::Edit : AccessType::View))
    {
        if (_set)
        {
            ttsbegin;

            select firstonly forupdate reqItemTable
            where  reqItemTable.ItemId          == _reqTrans.ItemId
                && reqItemTable.CovInventDimId  == _reqTrans.CovInventDimId;

            if (reqItemTable)
            {
                reqItemTable.PmfPlanPriorityCurrent = _current;
                reqItemTable.PmfPlanPriorityDateChanged = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
                reqItemTable.update();

                select firstonly forupdate updReqTrans
                where updReqTrans.RecId == _reqTrans.RecId;

                updReqTrans.PmfPlanPriorityCurrent = _current;
                updReqTrans.update();
                ttscommit;
                reqTrans_ds.reread();
                return updReqTrans.PmfPlanPriorityCurrent;
            }
            else
            {
                ttsabort;
                Box::warning("@PRO1475","@SYS88922");
                return _reqTrans.PmfPlanPriorityCurrent;
            }
        }

        return _reqTrans.PmfPlanPriorityCurrent;
    }

    return 0;
}

