public void init()
{
    super();
    ReqTransTransferIssue_ds.queryBuildDataSource().addLink(fieldNum(ReqTrans, ItemId), fieldNum(ReqTrans, ItemId));
}

public boolean validateWrite()
{
    InventDim currentDim = reqTransTransferIssue.inventDim();
    currentDim.InventLocationId = inventDimTransferIssue.InventLocationId;
    currentDim.initFromInventLocation(currentDim.inventLocation());

    // We do a reread of the transfer issue, because it could have been updated through a change on the reqPo (e.g. change of reqPo.Qty).
    reqTransTransferIssue.reread();
    reqTransTransferIssue.CovInventDimId = InventDim::findOrCreate(currentDim).InventDimId;

    return super();
}

public void write()
{
    // InventDim should not be written to directly.
}

public boolean validate()
{
    return super() &&
        reqPO.validateTransferOrder(inventDimTransferIssue.InventLocationId) &&
        ReqTransFormPO::validateFromInventLocationId(inventDimTransferIssue.InventLocationId, '', reqPo);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    // We remove siteId before calling super so that warehouses for all the sites will be shown.
    inventDimTransferIssue.InventSiteId = null;

    super(_formControl, _filterStr);
}

public void modified()
{
    super();

    if (ReqPOTransferLeadTimeFeature::isEnabled())
    {
        ReqPo.initLeadTime(ReqPo.reqSetupDim(), inventDimTransferIssue.InventLocationId);
        ReqPo.initFromReqDateDlv(ReqPo.reqSetupDim());
    }

}

