/// <summary>
/// Delegate raised when the asOfDate on the ResResourcesListPage is changed.
/// </summary>
/// <param name = "_formElement">The instance of the form run raising the delegate.</param>
/// <param name = "_asOfDateTime">The DateTime value used for date effective changes.</param>
/// <param name = "_showCurrentDate>true indicates that the date effective pane controller has been switched to the
/// current datetime value; otherwise the _asOfDateTime is used.</param>
delegate void dateEffectivePaneControllerUpdated(FormRun _formElement, utcdatetime _asOfDateTime, boolean _showCurrentDate)
{
}

/// <summary>
/// Event Handler for an Apply button (clicked) event in DateEffectivenessPaneController
/// </summary>
public void datePaneController_ApplyClicked()
{
    utcdatetime asOfDateTime = element.getFormAsOfDateTime();        
    boolean showCurrentDate = false;

    this.dateEffectivePaneControllerUpdated(this, asOfDateTime, showCurrentDate);

    // adjust the query for the delay joined data sources
    ResResourcesListView_DS.validTimeStateAutoQuery(ValidTimeStateAutoQuery::AsOfDate);
    ResResourcesListView_DS.query().validTimeStateAsOfDateTime(asOfDateTime);

    element.refreshControls();
}

/// <summary>
/// Event Handler for a Current button (clicked) event in DateEffectivenessPaneController to
/// view the currently active version record.
/// </summary>
public void datePaneController_CurrentClicked()
{
    boolean showCurrentDate = true;

    this.dateEffectivePaneControllerUpdated(this, DateTimeUtil::utcNow(), showCurrentDate);

    // adjust the query for the delay joined data sources
    ResResourcesListView_DS.validTimeStateAutoQuery(ValidTimeStateAutoQuery::AsOfDate);
    ResResourcesListView_DS.query().resetValidTimeStateQueryType();

    element.refreshControls();
}

/// <summary>
/// Refreshes the employment related controls on the HcmWorker form
/// </summary>
public void refreshControls()
{
    ResResourcesListView_DS.executeQuery();
}

/// <summary>
/// Add default resource category if it's none exists as well as populate records.
/// </summary>
public void init()
{
    super();

    ResUtil::validateResPerfDataUpgradeStatus();

    PSASchedRole::findOrCreateTeamMemberRole();
    xArgs formArguments = element.args();
    TableId lookupTable = formArguments.lookupTable();
    TableId resourceViewTableId = tableNum(ResourceView);

    if (lookupTable == resourceViewTableId)
    {
        str lookupValue = formArguments.lookupValue();
        ResourceView_DS.queryBuildDataSource().addRange(fieldNum(ResourceView, RecId)).value(lookupValue);
        ResResourcesListView_DS.queryBuildDataSource().addRange(fieldNum(ResResourcesListView, Resource)).value(lookupValue);
    }

    if (lookupTable == tableNum(ResourceWorkerView))
    {
        str lookupValue = formArguments.lookupValue();
        ResourceView resource;
        select resource where resource.RecId == str2Int64(lookupValue);
        formArguments.record(resource);
        formArguments.lookupTable(resourceViewTableId);
    }

    //Display navigation list if the caller is resource workspace
    Object callerFormObject = formArguments.caller();
    if (callerFormObject != null)
    {
        NavigationList.visible(false);
    }
    else
    {
        NavigationList.visible(true);
    }

    dePaneController = DateEffectivenessPaneController::constructWithForm(element, ResResourcesListView_DS, false, false, true);
    dePaneController.parmShowAsOfDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    dePaneController.onApplyClicked += eventhandler(this.datePaneController_ApplyClicked);
    dePaneController.onShowCurrentClicked += eventhandler(this.datePaneController_CurrentClicked);

    ResResourcesListView_DS.executeQuery();
}

