public utcdatetime getFormAsOfDateTime()
{
    utcdatetime asOfDateTime;
    utcdatetime nowDateTime = DateTimeUtil::utcNow();
    Timezone userTimeZone = DateTimeUtil::getUserPreferredTimeZone();

    if (dePaneController.parmShowAsOfDate() == DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(nowDateTime, userTimeZone)))
    {
        asOfDateTime = nowDateTime;
    }
    else
    {
        // set asOfDateTime to the end of the selected day
        asOfDateTime = DateTimeUtil::newDateTime(dePaneController.parmShowAsOfDate(), Global::timeMax(), userTimeZone);
    }

    return asOfDateTime;
}

public utcdatetime getFormAsOfDateTimeWithoutTimeZone()
{
    utcdatetime asOfDateTime;
    utcdatetime nowDateTime = DateTimeUtil::utcNow();

    if (dePaneController.parmShowAsOfDate() == DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(nowDateTime, 0)))
    {
        asOfDateTime = nowDateTime;
    }
    else
    {
        // set asOfDateTime to the end of the selected day
        asOfDateTime = DateTimeUtil::newDateTime(dePaneController.parmShowAsOfDate(), Global::timeMax(), 0);
    }

    return asOfDateTime;
}

/// <summary>
/// Disable certain function if the resource is not employed by this LE
/// </summary>
public void setActionPane()
{
    boolean employedInCurrentCompanyContext = ResResourcesListView.SourceCompany == curext();
    boolean isScheduable = ResResourcesListView.Schedulable == NoYes::Yes;

    // Only allow to setup default role if resource is in or loaned to current company
    boolean recipientInCurrentCompany = ResResourcesListView.RecipientCompany == curext();
    SetupDefaultResourceCategoryButton.enabled(recipientInCurrentCompany);

    ResourceSetup.enabled(employedInCurrentCompanyContext);
    CtrlCostPrice.enabled(employedInCurrentCompanyContext);
    ProjTransferPrice.enabled(employedInCurrentCompanyContext);
    ProjProject.enabled(employedInCurrentCompanyContext);
    ProjCategory.enabled(employedInCurrentCompanyContext);
    projectControl.enabled(employedInCurrentCompanyContext);
    projTransaction.enabled(employedInCurrentCompanyContext);
    salesPeriodStatus.enabled(employedInCurrentCompanyContext);
    DeleteResourceButton.enabled(employedInCurrentCompanyContext);
    UpdateResourceButton.enabled(employedInCurrentCompanyContext);
    Reserve.enabled(isScheduable);
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.setActionPane();
    }

    return ret;
}

