/// <summary>
/// Retrieves the worker name from HcmWorker record.
/// </summary>
display HcmWorkerName getWorkerName(ResResourcesListView _record)
{
    HcmWorkerName workerName;
    if (_record.Worker)
    {
        HcmWorker hcmWorkerRec = HcmWorker::find(_record.Worker);
        if (hcmWorkerRec)
        {
            workerName = hcmWorkerRec.name();
        }
    }
    return workerName;
}

public void init()
{
    super();
    this.queryBuildDataSource().addRange(fieldNum(ResResourcesListView, RecipientCompany)).value(curExt());
    this.queryBuildDataSource().addOrderByField(fieldNum(ResResourcesListView, ResourceName));
}

/// <summary>
/// Executes query to filter resources according to user selection.
/// </summary>
public void executeQuery()
{
    utcdatetime queryDateTime = element.getFormAsOfDateTimeWithoutTimeZone();

    QueryBuildDataSource resourceListDS = this.query().dataSourceTable(tableNum(ResResourcesListView));

    resourceListDS.clearRange(fieldNum(ResResourcesListView, EffectiveDate));
    resourceListDS.clearRange(fieldNum(ResResourcesListView, ExpirationDate));

    resourceListDS.addRange(fieldNum(ResResourcesListView, EffectiveDate))
        .value(queryRange(DateTimeUtil::minValue(), queryDateTime));
    resourceListDS.addRange(fieldNum(ResResourcesListView, ExpirationDate))
        .value(queryRange(queryDateTime, DateTimeUtil::maxValue()));

    if (resourceSchedulingType == ResResourceSchedulingType::All)
    {
        SysQuery::findOrCreateRange(resourceListDS, fieldNum(ResResourcesListView, Schedulable)).value(SysQuery::valueUnlimited());   
    }
    else if (resourceSchedulingType == ResResourceSchedulingType::Schedulable)
    {
        SysQuery::findOrCreateRange(resourceListDS, fieldNum(ResResourcesListView, Schedulable)).value(SysQuery::valueNot(0));
    }
    else
    {
        SysQuery::findOrCreateRange(resourceListDS, fieldNum(ResResourcesListView, Schedulable)).value(SysQuery::value(0));
    }

    ResourceView_DS.executeQuery();
}

/// <summary>
/// Displays the start date of resource based on employment history.
/// </summary>
display ValidFromDateTime resourceStartDate()
{
    HcmEmployment hcmEmployment;
    utcdatetime asOfDate = element.getFormAsOfDateTimeWithoutTimeZone();

    if (asOfDate && ResResourcesListView.Worker)
    {
        select firstonly validtimestate(asOfDate) ValidFrom from hcmEmployment order by ValidFrom
            where hcmEmployment.Worker == ResResourcesListView.Worker
            && hcmEmployment.LegalEntity == CompanyInfo::current();
    }

    return hcmEmployment.ValidFrom;
}

/// <summary>
/// Delete the resources records from <c>ResResourceDataEntity</c> data entity.
/// </summary>
public void clicked()
{
    ResResourcesListView        record;
    FormDataSource              ds = ResResourcesListView_DS;

    super();
    record = ds.getFirst(true) ? ds.getFirst(true) : ds.cursor();

    while (record)
    {
        ResourceFacade::deleteResource(record.Resource);
        record = ds.getNext();
    }
    element.refreshResourceList();
}

public void clicked()
{
    ResResourceSetupContract resourceSetupObject = new ResResourceSetupContract();
    resourceSetupObject.parmAsOfDateTimeWithTimeZone(element.getFormAsOfDateTime());

    Args args = new Args();
    args.caller(element);
    args.parmObject(resourceSetupObject);
    args.record(ResourceView);
    args.menuitemname(menuItemDisplayStr(ResourceSetup));
    args.name(formStr(ResourceSetup));
    FormRun formRun =  ClassFactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();
}

/// <summary>
/// Launch <c>ResAllocationView</c> form to show the resource reservation details.
/// </summary>
public void clicked()
{
    utcdatetime asOfDateTime = element.getFormAsOfDateTime();

    // Open full reservation details for the selected resource.
    ResAvailabilityViewContext context = ResAvailabilityViewContext::construct();
    context.Resource(ResResourcesListView.Resource);
    utcdatetime startDateTime = asOfDateTime;
    utcdatetime endDateTime = ResourceFacade::getLatestReservationDateTime(context.resource());
    endDateTime = endDateTime > startDateTime ? endDateTime : startDateTime;
    context.timescale(ResUtil::getRecommendedTimeframeType(startDateTime, endDateTime));
    context.startDateTime(startDateTime);
    context.endDateTime(endDateTime);
    context.showControlSingleResourceHeightParam(true);
    context.showFilterSettingsParam(false);
    context.showSearchButtonsParam(false);
    context.showForm();
}

public int selectionChange()
{
    // Refresh datasource according to user selection.
    int ret;

    ret = super();

    if (ret)
    {
        resourceSchedulingType = this.selection();
        ResResourcesListView_DS.executeQuery();
    }
    return ret;
}
