Method.Source:
[Form]
public class RestoredVATDistribution_RU extends FormRun
{
    RestoredVATLogTable_RU logTable;
    TransDate transDate;
    Map mapRestoredAmounts;

}

public void init()
{
    TransDate fromDate;

    if (element.args().dataset() == tableNum(RestoredVATLogTable_RU) && element.args().record())
    {
        logTable = element.args().record();
        transDate = logTable.TransDate;
    }
    else
    {
        [fromDate, transDate] = BookDataCalc_RU::datePeriodBook(SalesBookTable_RU::getLastClosedBook().ClosingDate + 1);
    }

    if (element.args().parmObject())
    {
        mapRestoredAmounts = element.args().parmObject();
    }
    else
    {
        mapRestoredAmounts  = Map::create(RestoredVATLogGain_RU::restoredAmounts(transDate));
    }

    super();
}

public display TaxAmountIndirectIncVAT20_RU taxVAT20IndirectIncAmount()
{
    return logTable.TaxStandardIndirectInc;
}

public display TaxAmountIndirectIncVAT10_RU taxVAT10IndirectIncAmount()
{
    return logTable.TaxReducedIndirectInc;
}

public void init()
{
    QueryBuildDataSource qbds;
    QueryBuildRange transDateRange;

    super();

    qbds = this.query().dataSourceTable(tableNum(RestoredVATSalesTaxTrans_RU));

    transDateRange = qbds.addRange(fieldNum(RestoredVATSalesTaxTrans_RU, TransDate));
    transDateRange.value(queryValue(transDate));
    transDateRange.status(RangeStatus::Hidden);
}

public void init()
{
    QueryBuildDataSource qbds;
    QueryBuildRange transDateRange;

    super();

    qbds = this.query().dataSourceTable(tableNum(CustInvoiceJour));
    qbds = qbds.addDataSource(tableNum(RestoredVATSalesTaxTrans_RU));
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.relations(true);

    transDateRange = qbds.addRange(fieldNum(RestoredVATSalesTaxTrans_RU, TransDate));
    transDateRange.value(queryValue(transDate));
    transDateRange.status(RangeStatus::Hidden);
}

display Factor factor(CustInvoiceJour _custInvoiceJour)
{
    Factor                      factor;
    TaxAmountDirectVAT20_RU     taxDirectVAT20;
    TaxAmountDirectVAT10_RU     taxDirectVAT10;
    TaxAmountIndirectVAT20_RU   taxIndirectVAT20;
    TaxAmountIndirectVAT10_RU   taxIndirectVAT10;
    ;

    if (mapRestoredAmounts.exists(_custInvoiceJour.RecId))
    {
        [factor,
         taxDirectVAT20,
         taxDirectVAT10,
         taxIndirectVAT20,
         taxIndirectVAT10] = mapRestoredAmounts.lookup(_custInvoiceJour.RecId);
    }

    return factor;
}

