[Form]
public class RestoredVATGain_RU extends FormRun
{
    Map mapGainCache;

}

public void init()
{
    super();

    if (element.args().parmObject())
    {
        mapGainCache = element.args().parmObject();
        mapGainCache = Map::create(mapGainCache.pack());
    }
    else
    {
        mapGainCache = new Map(Types::Int64, Types::Enum);
    }
}

/// <summary>
/// Returns a reference to the gain cache map.
/// </summary>
/// <returns>
/// A reference to the gain cache map.
/// </returns>
public Map getMapGainCache()
{
    return mapGainCache;
}

edit IncludeIntoCalc_RU markInclude(boolean _set, RestoredVATLogGain_RU _vatLogGain, IncludeIntoCalc_RU _includeNow)
{
    if (_set && _vatLogGain.RecId == restoredVATLogGain.RecId)
    {
        mapGainCache.insert(_vatLogGain.RecId, _includeNow);
    }

    if (mapGainCache.exists(_vatLogGain.RecId))
    {
        return mapGainCache.lookup(_vatLogGain.RecId);
    }

    return _vatLogGain.Include;
}

