void initGainCache()
{
    mapGainCache        = new Map(Types::Int64, Types::Enum);
    mapRestoredAmounts  = Map::create(RestoredVATLogGain_RU::restoredAmounts(toDate));

    factureAmounts      = new BookStructVATAmounts_RU();
    factureSmallAmounts = new BookStructSmallVATAmounts_RU();
}

void setButtons()
{
    boolean recalcEnabled = ! mapGainCache.empty() || restoredVATLogTable.RecalcIndirect;

    recalcNow.enabled(recalcEnabled);
    restoredVATApprove.enabled(!recalcEnabled && !restoredVATLogTable.RecalcAll);
}

