void updateDesign()
{
    boolean approved;
    ;

    restoredVATLogTable_ds.reread();
    restoredVATLogTable_ds.refresh();

    approved = restoredVATLogTable.Approved;

    restoredVATLogTrans_ds.allowEdit(!approved);
    restoredVATLogTransOper_ds.allowEdit(!approved);

    salesPurchInvoiceLinkUpdate.visible(!approved);
    restoredVATUpdate.visible(!approved);
    restoredVATApprove.visible(!approved);
    restoredVATCancel.visible(approved);

    restoredVATUpdate.enabled(toDate != dateNull() && SalesBookTable_RU::canProcessDate(toDate));
    restoredVATApprove.enabled(restoredVATLogTable.RecId     &&
                              !restoredVATLogTable.RecalcAll &&
                              !recalcNow.enabled());
}

