public int active()
{
    int     ret         = super();
    boolean isAsset     = restoredVATLogTransOper.Asset;
    boolean isIndirect  = restoredVATLogTransOper.LineType == RestoredVATLineType_RU::Indirect;
    ;

    factureSmallAmounts = BookStructSmallVATAmounts_RU::create(restoredVATLogTransOper.FacturedAmounts);

    this.object(fieldNum(RestoredVATLogTransOper_RU, TaxAmount)).allowEdit(isIndirect);
    this.object(fieldNum(RestoredVATLogTransOper_RU, TaxAmountNotLiable)).allowEdit(isIndirect);
    transOperInvoiceLines.visible(!isIndirect);
    transOperFixedAssets.visible(isAsset);

    return ret;
}

