void clicked()
{
    RestoredVATLogUpdate_RU restoredVATLogUpdate;

    super();

    if (! restoredVATLogTable.purchBookProcessingExists())
    {
        warning("@GLS114210");
    }

    restoredVATLogUpdate = RestoredVATLogUpdate_RU::newPeriod(fromDate, toDate);

    if (restoredVATLogUpdate.prompt())
    {
        restoredVATLogUpdate.runOperation();
    }

    restoredVATLogTable_ds.executeQuery();
}

void clicked()
{
    super();

    if (restoredVATLogTable.salesBookProcessingExists())
    {
        throw error("@GLS114226");
    }

    restoredVATLogTable.Approved    = NoYes::Yes;
    restoredVATLogTable.ApprovedBy  = curUserId();
    restoredVATLogTable.write();

    element.updateDesign();
}

void clicked()
{
    RestoredVATLogTable_RU logTable;

    super();

    if (restoredVATLogTable.salesBookProcessingExists())
    {
        throw error("@GLS114241");
    }

    if (RestoredVATLogTable_RU::purchBookExpConfirmExists(restoredVATLogTable.TransDate))
    {
        throw error("@GLS114240");
    }

    select firstonly logTable
        where logTable.TransDate > restoredVATLogTable.TransDate &&
              logTable.Approved;

    if (logTable)
    {
        throw error("@GLS114199");
    }

    restoredVATLogTable.Approved    = NoYes::No;
    restoredVATLogTable.ApprovedBy  = '';
    restoredVATLogTable.write();

    element.updateDesign();
}

public void clicked()
{
    Args args;
    FormRun formRun;

    args = new Args();
    args.record(restoredVATLogTable);
    args.parmObject(mapGainCache);

    formRun = new MenuFunction(menuItemDisplayStr(RestoredVATGain_RU), MenuItemType::Display).create(args);

    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        mapGainCache = (formRun as Object).getMapGainCache();
        element.setButtons();
    }
}

