public boolean modified()
{
    boolean ret = super();

    if (recalcNow.enabled())
    {
        if (Box::okCancel("@GLS114222", DialogButton::Cancel) != DialogButton::Ok)
        {
            datePeriodEdit.dateValue(toDate);
            ret = false;
        }
        else
        {
            restoredVATLogTable.RecalcIndirect = NoYes::Yes;
            restoredVATLogTable.write();
        }
    }

    if (ret)
    {
        [fromDate, toDate] = BookDataCalc_RU::datePeriodBook(this.dateValue());

        restoredVATLogTable_ds.executeQuery();
    }

    return ret;
}

