[Form]
public class RetailBusinessProcessStarterDialog extends FormRun
{
    Object                          dialogObj;
    RetailInternalOrgTreeDatasource internalOrgTreeDS;

}

/// <summary>
/// Populate the organization hierarchy combobox.
/// </summary>
/// <param name = "_hierarchyPurpose">The hierarchy purpose.</param>
/// <param name = "_checked">A parameter that indicates if the hierarchy is selected.</param>
/// <param name = "_hierarchyTypeRecId">The hierarchy type rec Id.</param>
public void populateNormalOrg(HierarchyPurpose _hierarchyPurpose, boolean _checked = true, RefRecId _hierarchyTypeRecId = 0)
{
    OMHierarchyPurpose  omHierarchyPurpose;
    OMHierarchyType     omHierarchyType;

    while select HierarchyType from omHierarchyPurpose
        where omHierarchyPurpose.HierarchyPurpose == _hierarchyPurpose
    {
        select firstonly Name from omHierarchyType
            where omHierarchyType.RecId == omHierarchyPurpose.HierarchyType;
        if (omHierarchyType && (_hierarchyTypeRecId != 0 ? _hierarchyTypeRecId == omHierarchyType.RecId : true))
        {
            if (!_checked)
            {
                StoresComboBoxHierarchy.delete(omHierarchyType.Name);
            }
            else
            {
                StoresComboBoxHierarchy.add(omHierarchyType.Name);
            }
        }
    }

    this.constructOrgTree(element.getOMHierarchyType(_hierarchyPurpose));
}

/// <summary>
/// Constructs the org tree of the hierarchy filters.
/// </summary>
/// <param name = "_hierarchyType">Selected hierarchy type.</param>
public void constructOrgTree(OMHierarchyType _hierarchyType)
{
    internalOrgTreeDS = RetailInternalOrgTreeDatasource::construct(StoresTree, _hierarchyType);
    internalOrgTreeDS.buildTree();
}

/// <summary>
/// Refreshes OMExplodedOrganizationSecurityGraph data source by the specific field.
/// </summary>
/// <param name = "_fieldId">The ID of the given field.</param>
/// <param name = "_fieldValue">The value of the given field.</param>
internal void refreshOMExplodedOrganizationSecurityGraphDS(FieldId _fieldId, RecId _fieldValue)
{
    OMExplodedOrganizationSecurityGraph_ds
        .queryBuildDataSource()
        .clearRange(_fieldId);
    OMExplodedOrganizationSecurityGraph_ds
        .queryBuildDataSource()
        .addRange(_fieldId)
        .value(SysQuery::value(_fieldValue));
    OMExplodedOrganizationSecurityGraph_ds.executeQuery();
}

public OMHierarchyType getOMHierarchyType(HierarchyPurpose _hierarchyPurpose)
{
    OMHierarchyPurpose  omHierarchyPurpose;
    OMHierarchyType     omHierarchyType;

    select firstonly RecId from omHierarchyType
        where omHierarchyType.Name == StoresComboBoxHierarchy.valueStr()
            join HierarchyType, HierarchyPurpose from omHierarchyPurpose
            where omHierarchyPurpose.HierarchyType == omHierarchyType.RecId
                && omHierarchyPurpose.HierarchyPurpose == _hierarchyPurpose;
    return omHierarchyType;
}

/// <summary>
/// initialize the dialog
/// </summary>
public void init()
{   
    super();

    element.populateNormalOrg(HierarchyPurpose::RetailPOSPosting, true);

    OMExplodedOrganizationSecurityGraph_ds
        .queryBuildDataSource()
        .addRange(fieldNum(OMExplodedOrganizationSecurityGraph, OMHierarchyType))
        .value(SysQuery::value(element.getOMHierarchyType(HierarchyPurpose::RetailPOSPosting).RecId));

    RetailStoreTable_ds
        .queryBuildDataSource()
        .addSortField(fieldNum(RetailStoreTable, StoreNumber));

    RetailStoreTable_ds
        .queryBuildDataSource()
        .addLink(fieldNum(OMExplodedOrganizationSecurityGraph, Organization), fieldNum(RetailStoreTable, OMOperatingUnitID));

    DirPerson_ds
        .queryBuildDataSource()
        .addLink(fieldNum(HcmWorker, Person), fieldNum(DirPerson, RecId));

    this.setEditMode();
    this.setControlVisibility();
}

public SysOperationDialog dialog()
{
    if (dialogObj == null)
    {
        dialogObj = Dialog::getDialogFromCaller(this);
    }
    return dialogObj;
}

public void closeOK()
{     
    RetailTmpBusinessProcessStarterStore    selectedStore;
    RetailTmpBusinessProcessStarterWorker   selectedWorker;
    RetailBusinessProcessStarterController  controller;
    Set                                     channelIds;
    Set                                     workerIds;

    controller  = this.dialog().controller();

    channelIds  = new Set(Types::Int64);
    workerIds   = new Set(Types::Int64);

    while select retailTmpBusinessProcessStarterStore
    {
        channelIds.add(retailTmpBusinessProcessStarterStore.StoreRecId);
    }

    while select retailTmpBusinessProcessStarterWorker
    {
        workerIds.add(RetailTmpBusinessProcessStarterWorker.HcmWorkerRecId);
    }

    controller.populateDataContract(ProcessName.text(),
                                    RecurrenceCheckBox.value(),
                                    TargetDate.dateValue(),
                                    TargetDateOffset.value(),
                                    channelIds,
                                    workerIds);

    if (controller.checkCloseDialog())
    {             
        controller.writeProcess();

        super();

        if (controller.inCreateMode())
        {
            info(strFmt("@RetailTaskManagement:RetailTaskManagementStartProcessConfirmation", ProcessName.text()));
        }
    }
}

public void setEditMode()
{
    RetailBusinessProcessStarterController controller = this.dialog().controller();
    if (controller.inEditMode())
    {   
        RetailBusinessProcessStarterContract contract = controller.getDataContractObject();

        ProcessName.text(contract.parmProcessName());
        TargetDate.dateValue(contract.parmTargetDate());
        TargetDateOffset.value(contract.parmRecurrentTargetDateOffset());
        RecurrenceCheckBox.value(contract.parmRecurrence());

        ProcessName.allowEdit(false);
        RecurrenceCheckBox.allowEdit(false);

        // one time process won't allow edit
        if(!contract.parmRecurrence())
        {                
            FormTabControl tab = element.design(0).controlName(formControlStr(RetailBusinessProcessStarterDialog, Tab));
            tab.enabled(false);

            FormCommandButtonControl OkButton = element.design(0).controlName(formControlStr(RetailBusinessProcessStarterDialog, OkButton));
            OkButton.enabled(false);
        }

        SetEnumerator channelIdsEnumerator = contract.parmSelectedChannelRecIds().getEnumerator();

        while (channelIdsEnumerator.moveNext())
        {
            RecId channelRecId = channelIdsEnumerator.current();

            RetailStoreTable storeTable = RetailStoreTable::findRecId(channelRecId);

            if (storeTable)
            {
                retailTmpBusinessProcessStarterStore.clear();
                retailTmpBusinessProcessStarterStore.StoreRecId         = storeTable.RecId;
                retailTmpBusinessProcessStarterStore.RetailStoreName    = storeTable.name();
                retailTmpBusinessProcessStarterStore.RetailStoreId      = storeTable.StoreNumber;
                retailTmpBusinessProcessStarterStore.insert();
            }
        }

        SetEnumerator workerIdsEnumerator = contract.parmSelectedWorkerRecIds().getEnumerator();

        while (workerIdsEnumerator.moveNext())
        {
            RecId hcmWorkerRecId = workerIdsEnumerator.current();

            HcmWorker worker = HcmWorker::find(hcmWorkerRecId);

            if (worker)
            {
                retailTmpBusinessProcessStarterWorker.clear();
                retailTmpBusinessProcessStarterWorker.HcmWorkerRecId    = worker.RecId;
                retailTmpBusinessProcessStarterWorker.PersonnelNumber   = worker.PersonnelNumber;
                retailTmpBusinessProcessStarterWorker.Name              = worker.name();
                retailTmpBusinessProcessStarterWorker.insert();
            }
        }
    }
}

public void setControlVisibility()
{
    boolean setRecurrence = RecurrenceCheckBox.value() == NoYes::Yes;

    OneTimeGroup.visible(!setRecurrence);
    RecurrenceGroup.visible(setRecurrence);

    // clear the onetime group value when recurrence is turned on
    if (!OneTimeGroup.visible())
    {
        TargetDate.dateValue(dateNull());
    }
    if (!RecurrenceGroup.visible())
    {
        TargetDateOffset.value(0);
    }

    // this batch control is generated in runtime
    FormTabPageControl recurrentTab = element.design(0).controlName('TabPg_1');
    if (recurrentTab)
    {
        RetailBusinessProcessStarterController controller = this.dialog().controller();
        recurrentTab.visible(setRecurrence && !controller.inEditMode());
    }

    FormCheckBoxControl batchCheckbox = element.design(0).controlName('Fld1_1');
    if (batchCheckbox)
    {
        batchCheckbox.allowEdit(false);
        batchCheckbox.value(setRecurrence);
    }
}

/// <summary>
/// override the method to adjust corresponding one-time or recurrence group control visibility
/// </summary>
/// <returns>the boolean value indicates if the value has been modified</returns>
public boolean modified()
{
    boolean ret = super();

    element.setControlVisibility();

    return ret;
}

/// <summary>
/// Executes after the selection of a different node of the tree.
/// </summary>
/// <param name = "_OldItem">The previously selected tree node.</param>
/// <param name = "_NewItem">The newly selected tree node.</param>
/// <param name = "_how">The method of node selection.</param>
public void selectionChanged(FormTreeItem _OldItem, FormTreeItem _NewItem, FormTreeSelect _how)
{
    super(_OldItem, _NewItem, _how);

    int64 organizationHierarchyRecId = _NewItem.data();

    element.refreshOMExplodedOrganizationSecurityGraphDS(fieldNum(OMExplodedOrganizationSecurityGraph, AncestorOrganization), organizationHierarchyRecId);
}

public void clicked()
{
    super();

    FormDataSource                          fds;
    RetailStoreTable                        rst;
    RetailTmpBusinessProcessStarterStore    retailTmpBusinessProcessStarterStore_local;

    retailTmpBusinessProcessStarterStore_local.linkPhysicalTableInstance(retailTmpBusinessProcessStarterStore);

    fds = FormDataUtil::getFormDataSource(RetailStoreTable);

    for (rst = fds.getFirst(true) ? fds.getFirst(true) : fds.cursor(); rst; rst = fds.getNext())
    {
        select firstonly retailTmpBusinessProcessStarterStore_local
            where retailTmpBusinessProcessStarterStore_local.RetailStoreId == rst.StoreNumber;

        if (!retailTmpBusinessProcessStarterStore_local)
        {
            retailTmpBusinessProcessStarterStore.clear();
            retailTmpBusinessProcessStarterStore.RetailStoreId      = rst.StoreNumber;
            retailTmpBusinessProcessStarterStore.RetailStoreName    = rst.name();
            retailTmpBusinessProcessStarterStore.StoreRecId         = rst.RecId;
            retailTmpBusinessProcessStarterStore.insert();
        }
    }

    RetailTmpBusinessProcessStarterStore_ds.research(true);
}

public void clicked()
{
    super();

    FormDataSource                          fds;
    HcmWorker                               hw;
    RetailTmpBusinessProcessStarterWorker   retailTmpBusinessProcessStarterWorker_local;

    retailTmpBusinessProcessStarterWorker_local.linkPhysicalTableInstance(retailTmpBusinessProcessStarterWorker);

    fds = FormDataUtil::getFormDataSource(HcmWorker);

    for (hw = fds.getFirst(true) ? fds.getFirst(true) : fds.cursor(); hw; hw = fds.getNext())
    {
        select firstonly retailTmpBusinessProcessStarterWorker_local
            where retailTmpBusinessProcessStarterWorker_local.HcmWorkerRecId == hw.RecId;

        if (!retailTmpBusinessProcessStarterWorker_local)
        {
            retailTmpBusinessProcessStarterWorker.clear();
            retailTmpBusinessProcessStarterWorker.HcmWorkerRecId   = hw.RecId;
            retailTmpBusinessProcessStarterWorker.PersonnelNumber  = hw.PersonnelNumber;
            RetailTmpBusinessProcessStarterWorker.Name             = hw.name();
            retailTmpBusinessProcessStarterWorker.insert();
        }
    }

    RetailTmpBusinessProcessStarterWorker_ds.research(true);
}
