using Microsoft.Dynamics.Commerce.Headquarters.Instrumentation;

[Form]
public class RetailCDXDataStore extends FormRun
{
    QueryBuildRange dataStoreTypeRange;
    str maskedConnectionString = '97E4D4CB-BDBF-46E0-8554-301EED1D383A';
    RetailSelfServiceMergedPackageManager selfServicePackageManager;
    private static CommerceHQEventSource logger = CommerceHQEventSource::Log;

}

public void init()
{
    super();

    RetailConnectionStringEncrypted.text(maskedConnectionString);
    RetailSelfServiceMigrationHelper::populateDefaultPackageReferencesFromRetailSharedParameters();
    selfServicePackageManager = new RetailSelfServiceMergedPackageManager();

    /// Show warning of Data Group channels that are not mapped to a channel database, but mapped to an offline database.
    RetailCdxDataStoreChannel::checkUnmappedChannelDataGroup(true);
}

/// <summary>
/// Enable or disable fields based on the current selected record values.
/// </summary>
public void setDatabaseProfileFieldsAccess()
{
    // Currently store system is not supported for sql azure database and offline database, disable this feature for this two kinds of database profile.
    if (RetailDeploymentUtility::isStoreSystemSupported(RetailConnDatabaseProfile))
    {
        DownloadButtonGroup.enabled(true);
        RSSUSelfServiceReferenceTab.visible(true);
    }
    else
    {
        DownloadButtonGroup.enabled(false);
        RSSUSelfServiceReferenceTab.visible(false);
    }

    // Only display legacy client connection group when the schema type is AX63
    LegacyClientConnection.visible(retailConnDatabaseProfile.getSchemaType() == RetailConnChannelSchemaTypeEnum::AX63);

    // Do not allow editing default channel database name which was generated in the deployment process, and the environment reprovisioning tool expects this to be present
    RetailConnDatabaseProfile_Name1.allowEdit(!(retailConnDatabaseProfile.Name == 'Default'));
}

private boolean validateDataStoreName()
{
    str dataStoreName;
    System.Text.RegularExpressions.Match regExMatch;

    dataStoreName = RetailConnDatabaseProfile_Name1.valueStr();
    // Verify that channel database name doesnâ€™t contain bad special character
    // bad special characters included in the check are <>:"/\|?*&!@#$%^(){}[];~`=+
    regExMatch = System.Text.RegularExpressions.Regex::Match(dataStoreName, @'^[^<>:"/\\|?*\&!@#\$\%\^\(\)\{\}\[\];\~\`=+]*$');

    if (!regExMatch.get_Success())
    {
        throw Global::error("@REX4161199");
    }

    return regExMatch.get_Success();
}

public boolean validateWrite()
{
    boolean ret;
    str changedConnectionString;

    ret = super();

    if (ret)
    {
        ret = retailConnDatabaseProfile_ds.validateDataStoreName();
    }

    // Encrypt and store connection string's value if it has changed.
    changedConnectionString = RetailConnectionStringEncrypted.valueStr();
    if (strCmp(strLTrim(strRTrim(changedConnectionString)), maskedConnectionString) != 0)
    {
        RetailConnDatabaseProfile.connectionStringEdit(true, changedConnectionString);
        RetailConnectionStringEncrypted.text(maskedConnectionString);
    }

    return ret;
}

public void init()
{
    QueryBuildDataSource qbds;
    RetailChannelTable retailChannelTable;

    super();
    dataStoreTypeRange = this.queryBuildDataSource().AddRange(fieldNum(RetailConnDatabaseProfile, DataStoreType));

    if (element.args().record() && element.args().record() is RetailChannelTable)
    {
        retailChannelTable = element.args().record();
        qbds = this.query().dataSourceTable(this.table()).addDataSource(RetailCDXDataStoreChannel);
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.relations(true);
        qbds.addDynalink(fieldNum(RetailCDXDataStoreChannel, Channel), retailChannelTable, fieldNum(RetailChannelTable, RecId));
    }
}

public void selectionChanged()
{
    super();

    boolean decryptionExceptionCaught = this.hasDecryptionError(RetailConnDatabaseProfile, false);

    if (!decryptionExceptionCaught)
    {
        element.setDatabaseProfileFieldsAccess();
    }
}

/// <summary>
/// Active function for this data source.
/// </summary>
/// <returns>Returns an integer outcome indicating if the method was successful.</returns>
public int active()
{
    int ret;
    ret = super();

    // Decrypt the connection string, mask the password and set in connection string label.
    this.setDecryptedConnectionString();

    boolean decryptionExceptionCaught = this.hasDecryptionError(RetailConnDatabaseProfile, true);

    if (!decryptionExceptionCaught)
    {
        element.setDatabaseProfileFieldsAccess();
    }

    // Table RetailConnDatabaseProfile has a new field merged self-service reference package.
    // Call the method below in case the new field does not have any value, but the old field does
    // since now we're showing the new field on this UI and make the legacy field invisible.
    // new field (visible): RetailConnDatabaseProfile.MergedSelfServicePackageReference
    // legacy field (hidden): RetailConnDatabaseProfile.SelfServicePackage
    RetailSelfServiceMigrationHelper::populateMergedPackageReferenceFromSelfServicePackageReference(
        this.cursor(),
        fieldNum(RetailConnDatabaseProfile, SelfServicePackage),
        fieldNum(RetailConnDatabaseProfile, MergedSelfServicePackageReference));

    this.enableEditDelete();

    RefGroupDataWritingBatchJob.enabled(!(RetailConnDatabaseProfile.ConnectionString == ''));
    DataWritingBatchJobStatus.enabled(!(RetailConnDatabaseProfile.ConnectionString == ''));

    // Show data sync interval only for RSSU or MPOS offline
    RetailCDXSchedulerInterval.enabled(RetailDeploymentUtility::isStoreSystemSupported(RetailConnDatabaseProfile) || RetailConnDatabaseProfile.DataStoreType == RetailCDXDataStoreType::OfflineDatabase);
    return ret;
}

private boolean hasDecryptionError(RetailConnDatabaseProfile currentRecord, boolean showWarningErrors)
{
    boolean decryptionExceptionCaught = false;

    try
    {
        currentRecord.connectionStringEdit(false, '');
    }
    catch (Exception::CLRError)
    {
        decryptionExceptionCaught = true;
        System.Exception clrException = RetailUtilities::getLatestClrException();
        str exceptionMessage = clrException.get_Message();
        str stackTrace = clrException.get_StackTrace();

        if (showWarningErrors)
        {
            Global::warning(strFmt("@RetailSelfServiceInstaller:DecryptionErrorOnRetailConnDbProfile", exceptionMessage));
        }

        logger.EventWriteSelfServiceUpdateChannelDatabaseFailed(
            currentRecord.Name, exceptionMessage, stackTrace);
    }

    return decryptionExceptionCaught;
}

private void setDecryptedConnectionString()
{
    var connString = '';

    try
    {
        // Mask any senitive data with key and value like Password or User ID.
        connString = RetailConnDatabaseProfile.connectionStringEdit(false, "");
        if (strLen(connString) != 0)
        {
            DecryptedConnectionString.text(this.getSimpleConnectionString(connString));
        }
        else
        {
            DecryptedConnectionString.text("@Retail:CDXEmptyConnectionString");
        }
    }
    catch
    {
        DecryptedConnectionString.text("@Retail:CDXInvalidConnectionString");
    }
}

private str getSimpleConnectionString(str connectionString)
{
    if (strLen(connectionString) == 0)
    {
        return "@Retail:CDXEmptyConnectionString";
    }

    try
    {
        System.Data.SqlClient.SqlConnectionStringBuilder connectionStringBuilder;
        str  connStr;

        connectionStringBuilder = new System.Data.SqlClient.SqlConnectionStringBuilder();
        connectionStringBuilder.ConnectionString = connectionString;

        if (connectionStringBuilder.Password != null && strLen(connectionStringBuilder.Password) != 0)
        {
                connectionStringBuilder.Password = '********';
        }

        //Create connectionString
        connStr = connectionStringBuilder.get_ConnectionString();
        return connStr;
    }
    catch
    {
        // If connection string is not a valid connection string, return invalid connection string label.
        return "@Retail:CDXInvalidConnectionString";
    }
}

private void enableEditDelete()
{
    // do not allow deleting default online channel database
    boolean isDefaultOnlineChannel = RetailConnDatabaseProfile.DataStoreType == RetailCDXDataStoreType::ChannelDatabase
        && RetailConnDatabaseProfile.ConnectionString;

    RetailScaleUnitConfigurationMap configurationMap = RetailConnDatabaseProfile;
    boolean allowEditDeleteScaleUnitConfiguration = !configurationMap.RetailScaleUnitConfigurationMap::isScaleUnitSystemRecord();

    // we should always allow changing only the database group
    DictTable   dictTable = new DictTable(RetailConnDatabaseProfile.TableId);

    for (int i = 1; i <= dictTable.fieldCnt(); i++)
    {
        int fieldNumber = dictTable.fieldCnt2Id(i);

        if (RetailConnDatabaseProfile_ds.object(fieldNumber) &&
            fieldNumber != fieldNum(RetailConnDatabaseProfile, DataGroup) &&
            fieldNumber != fieldNum(RetailConnDatabaseProfile, PauseDataSync) &&
            fieldNumber != fieldNum(RetailConnDatabaseProfile, AllowUploadDuringSyncPause))
        {
            RetailConnDatabaseProfile_ds.object(fieldNumber).allowEdit(allowEditDeleteScaleUnitConfiguration);
        }
    }

    RetailConnDatabaseProfile_ds.allowDelete(!isDefaultOnlineChannel && allowEditDeleteScaleUnitConfiguration);
    RetailConnDatabaseProfile_ds.object(fieldNum(RetailConnDatabaseProfile, AllowUploadDuringSyncPause)).allowEdit(RetailConnDatabaseProfile.PauseDataSync);
}

/// <summary>
/// Changing the channel data base group drop-down, it will show the warning.
/// </summary>
public void write()
{
    super();

    if (RetailCdxUnmappedChannelStripMasterDataFlight::instance().isEnabled()
        && RetailCdxDataStoreChannel::getUnmappedChannelCount(RetailConnDatabaseProfile.DataGroup) > 0)
    {
        error("@Retail:RetailStripMasterDataChannelMapWarning");
    }
}

/// <summary>
/// Updates AllowUploadDuringSyncPause when PauseDataSync is modified.
/// </summary>
public void modified()
{
    super();

    if (!RetailConnDatabaseProfile.PauseDataSync)
    {
        RetailConnDatabaseProfile.AllowUploadDuringSyncPause = NoYes::No;
    }

    RetailConnDatabaseProfile_ds.object(fieldNum(RetailConnDatabaseProfile, AllowUploadDuringSyncPause)).allowEdit(RetailConnDatabaseProfile.PauseDataSync);
}

/// <summary>
/// Running multiple full syncs in parallel can impact HQ database and affect user operations.
/// We recommend to run full sync using the new "Full data sync" buttons added to "Channel database" and "Offline database" grids in "Channel database group" form,
/// which will generate a single package for all selected databases. Select 'Yes' to continue with legacy full sync (not recommended), or 'No' to cancel it so you can use the new button.
/// </summary>
public void clicked()
{
    if (Box::yesNo("@Retail:FullSyncFromDataStoreGroup", DialogButton::No) == DialogButton::Yes)
    {
        super();
    }
}

/// <summary>
/// Download Modern POS setup file.
/// </summary>
public void clicked()
{
    super();

    str connString = RetailConnDatabaseProfile.connectionStringEdit(false, "");
    if (RetailDeploymentUtility::isSqlAzureConnectionString(connString) || RetailConnDatabaseProfile.DataStoreType == RetailCDXDataStoreType::OfflineDatabase)
    {
        throw Global::error("@RETAIL:RetailStoreScaleUnitNotSupportedForDefaultAndOffline");
    }

    if (RetailSelfServiceMergedPackageManager::validateDownloadPackageReference(RetailConnDatabaseProfile, fieldNum(RetailConnDatabaseProfile, MergedSelfServicePackageReference)))
    {
        str packageDownloadUrl = selfServicePackageManager.generatePackageDownloadLink(RetailConnDatabaseProfile.MergedSelfServicePackageReference, PackageType::RetailStoreScaleUnit, true);

        // Opens new tab and navigate to specified url
        RetailSelfServicePackageManager::OpenLinkInNewBrowserTab(packageDownloadUrl, "@RETAIL:SelfServicePkgRetrievalError");
    }
}

/// <summary>
/// Download modern pos configuration xml file.
/// </summary>
public void clicked()
{
    super();

    str connString = RetailConnDatabaseProfile.connectionStringEdit(false, "");
    if (RetailDeploymentUtility::isSqlAzureConnectionString(connString) || RetailConnDatabaseProfile.DataStoreType == RetailCDXDataStoreType::OfflineDatabase)
    {
        throw Global::error("@RETAIL:RetailStoreScaleUnitNotSupportedForDefaultAndOffline");
    }

    // Download modern pos configuration xml
    RetailSelfServicePackageManager::DownloadConfigXml(RetailConnDatabaseProfile.RecId, PackageType::RetailStoreScaleUnit);
}

/// <summary>
/// If data store type filter is changed, update using selected filter.
/// </summary>
/// <returns>Returns an integer outcome indicating if the method was successful.</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    str rangeVal;
    if (this.selection() == RetailCDXDataStoreTypeDisplay::AllDatabases)
    {
        rangeVal = "";
    }
    else
    {
        rangeVal = int2Str(this.selection());
    }

    dataStoreTypeRange.value(rangeVal);
    RetailConnDatabaseProfile_DS.executeQuery();
    return ret;
}

/// <summary>
/// Allow direct navigation from the RetailChannel field to its corresponding details page.
/// </summary>
public void jumpRef()
{
    RetailChannelTable channel;
    Args    args = new Args();
    MenuFunction menuFunction;

    select firstonly ChannelType, RecId
        from channel
        where channel.RecId == RetailCDXDataStoreChannel.Channel;

    switch (channel.ChannelType)
    {
        case RetailChannelType::RetailStore:
            menuFunction = new MenuFunction(menuitemDisplayStr(RetailStoreTable), MenuItemType::Display);
            args.record(RetailStoreTable::findRecId(channel.RecId));
            break;

        case RetailChannelType::SharePointOnlineStore:
            menuFunction = new MenuFunction(menuitemDisplayStr(RetailOnlineChannels), MenuItemType::Display);
            args.record(RetailOnlineChannelTable::find(channel.RecId));
            break;

        case RetailChannelType::MCRCallCenter:
            menuFunction = new MenuFunction(menuitemDisplayStr(RetailCallCenters), MenuItemType::Display);
            args.record(RetailMCRChannelTable::findRecId(channel.RecId));
            break;

        default:
            return;
    }

    menuFunction.run(args);
}

/// <summary>
/// To filter lookup based on the channeltype.
/// </summary>
/// <returns>
/// A record in the <c>RetailChannelTable</c> table that is selected by a user.
/// </returns>
public Common lookupReference()
{
    Common ret;
    SysReferenceTableLookup sysReferenceTableLookup;
    Query query = new Query();

    sysReferenceTableLookup = sysReferenceTableLookup::newParameters(tableNum(RetailChannelTable),this);

    if (RetailConnDatabaseProfile.Name != RetailCustomerSearchConstants::CdxDatabaseProfileName)
    {
        query.addDataSource(tableNum(RetailChannelTable)).addRange
            (fieldNum(RetailChannelTable, ChannelType)).value(SysQuery::valueNot(RetailChannelType::MCRCallCenter));
    }

    else
    {
        query.addDataSource(tableNum(RetailChannelTable)).addRange 
            (fieldNum(RetailChannelTable, RecId)).value(SysQuery::valueNot(0));
    }

    SysReferenceTableLookup.addLookupfield(fieldNum(RetailChannelTable, OMOperatingUnitID));

    sysReferenceTableLookup.parmQuery(query);

    ret = sysReferenceTableLookup.performFormLookup();

    return ret;
}

