public void createDiscountCode()
{
    if (retailPeriodicDiscount.DiscountCode)
    {
        retailCampaignDiscountMedia.DiscountCode = retailPeriodicDiscount.DiscountCode;
    }
    else
    {
        retailDiscountCode_DS.create();
        retailDiscountCode.DiscountOfferId = retailPeriodicDiscount.OfferId;
        retailDiscountCode_DS.write();
        retailCampaignDiscountMedia.DiscountCode = retailDiscountCode.RecId;
    }
}

public void deleteDiscountCode()
{
    RefRecId discountCodeRecId = retailCampaignDiscountMedia.DiscountCode;
    retailCampaignDiscountMedia.DiscountCode = 0;
    retailCampaignDiscountMedia.update();

    RetailDiscountCode::find(discountCodeRecId, true).deleteIfOrphaned();
}

