    }
}

// populate temp table with available media types for the current campaign
public void initAvailableMedia()
{
    smmCampaignMediaType        availableMedia;
    RetailCampaignDiscountMedia currentMedia;
    // read the necessary info using query
    QueryRun                    qr = this.initMediaQuery();

    // clear current data, if any
    delete_from campaignMedia;

    qr.reset();
    while (qr.next())
    {
        availableMedia = qr.get(tableNum(smmCampaignMediaType));
        currentMedia = qr.get(tableNum(RetailCampaignDiscountMedia));

        campaignMedia.clear();
        campaignMedia.CampaignMediaTypeId = availableMedia.CampaignMediaTypeId;
        campaignMedia.FromDate = availableMedia.FromDate;
        campaignMedia.ToDate = availableMedia.ToDate;
        // if currentmedia.RecId is zero, then this media type is not selected for the current discount offer
        campaignMedia.IsSelected = currentMedia.RecId > 0;
        campaignMedia.insert();
    }
}

private QueryRun initMediaQuery()
{
    QueryRun result;
    QueryBuildDataSource qbdsMain, qbdsChild;

    Query campaignMediaQuery = new Query();

    // creating a query like smmCampaignMediaType left join RetailCampaignDiscountMedia
    qbdsMain = campaignMediaQuery.addDataSource(tableNum(smmCampaignMediaType));
    qbdsChild = qbdsMain.addDataSource(tableNum(RetailCampaignDiscountMedia));

    // apply range for the current campaign
    qbdsMain.addRange(fieldNum(smmCampaignMediaType, CampaignId)).value(RetailCampaignDiscount.Campaign);
    // apply date range
    qbdsMain
        .addRange(fieldNum(smmCampaignMediaType, FromDate))
        .value(strFmt('((FromDate == %1) || (FromDate <= %2)) && ((ToDate == %1) || (ToDate >= %2)))', date2StrXpp(dateNull()), date2StrXpp(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))));
    qbdsChild.relations(true);
    qbdsChild.joinMode(JoinMode::OuterJoin);
    // apply range for the current discount offer
    qbdsChild.addRange(fieldNum(RetailCampaignDiscountMedia, CampaignDiscount)).value(queryValue(RetailCampaignDiscount.RecId));
    result = new QueryRun(campaignMediaQuery);

    return result;
}

NumberSeqFormHandler numberSeqDiscountCode()
{
    if (!numberSeqDiscountCode)
    {
        numberSeqDiscountCode = NumberSeqFormHandler::newForm(RetailParameters::numRefDiscountCode().NumberSequenceId,
                                                         element,
