public void write()
{
    smmCampaignMediaType oldCampaignMediaType;
    smmCampaignMediaType newCampaignMediaType;

    if (retailPeriodicDiscount.IsDiscountCodeRequired
        && !RetailCampaignDiscountMedia.DiscountCode)
    {
        element.createDiscountCode();
    }

    if (RetailCampaignDiscountMedia.Media != RetailCampaignDiscountMedia.orig().Media)
    {
        select firstOnly CampaignMediaTypeId from oldCampaignMediaType
            where oldCampaignMediaType.RecId == RetailCampaignDiscountMedia.orig().Media;
        element.updateCampaignMedia(oldCampaignMediaType.CampaignMediaTypeId, false);

        select firstOnly CampaignMediaTypeId from newCampaignMediaType
            where newCampaignMediaType.RecId == RetailCampaignDiscountMedia.Media;
        element.updateCampaignMedia(newCampaignMediaType.CampaignMediaTypeId, true);
    }

    super();
}

