}

edit smmCampaignMediaTypeId mediaType(boolean set, RetailCampaignDiscountMedia _retailCampaignDiscountMedia, smmCampaignMediaTypeId mediaTypeId)
{
    smmCampaignMediaType    smmCampaignMediaType;
    RetailCampaignDiscount  campaignDiscount;

    if (set)
    {
        select firstonly RecId, Campaign from campaignDiscount
            where campaignDiscount.RecId == retailCampaignDiscount.RecId;

        select firstonly smmCampaignMediaType
            where smmCampaignMediaType.CampaignId == campaignDiscount.Campaign &&
                  smmCampaignMediaType.CampaignMediaTypeId == mediaTypeId &&
                  ((smmCampaignMediaType.FromDate == dateNull()) || (smmCampaignMediaType.FromDate <= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))) &&
                  ((smmCampaignMediaType.ToDate == dateNull()) || (smmCampaignMediaType.ToDate >= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())));

        retailCampaignDiscountMedia.Media = smmCampaignMediaType.RecId;
    }
    else
    {
        select firstonly CampaignMediaTypeId from smmCampaignMediaType
            where smmCampaignMediaType.RecId == _retailCampaignDiscountMedia.Media;
    }

    return smmCampaignMediaType.CampaignMediaTypeId;
}

