void clicked()
{
    smmCampaignMediaType availableMedia;

    // check for the existing media within the campaign
    select firstOnly CampaignId, RecId from availableMedia
        where availableMedia.CampaignId == RetailCampaignDiscount.Campaign;

    if (availableMedia.RecId == 0)
    {
        // if there are no media, throw a warning
        warning("@RET260820");
        return;
    }
    else
    {
        super();
    }
}

void clicked()
{
    super();

    if (RetailPeriodicDiscount.IsDiscountCodeRequired)
    {
        if (!retailCampaignDiscountMedia.DiscountCode)
        {
            element.createDiscountCode();
        }
    }
    else
    {
        if (retailCampaignDiscountMedia.DiscountCode)
        {
            element.deleteDiscountCode();
        }
    }

    retailCampaignDiscountMedia_DS.research(true);
}

public void lookup()
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;
    SysTableLookup          tableLookup = SysTableLookup::newParameters(tableNum(RetailTmpCampaignDiscountMedia), this);
    ;

    tableLookup.parmCallingControl(this);
    tableLookup.parmUseLookupValue(true);
    // Add fields to be shown in the lookup form.
    tableLookup.addLookupfield(fieldNum(RetailTmpCampaignDiscountMedia, campaignMediaTypeId));
    tableLookup.addLookupfield(fieldNum(RetailTmpCampaignDiscountMedia, FromDate));
    tableLookup.addLookupfield(fieldNum(RetailTmpCampaignDiscountMedia, ToDate));
    tableLookup.addLookupfield(fieldNum(RetailTmpCampaignDiscountMedia, Description));

    queryBuildDataSource = query.addDataSource(tableNum(RetailTmpCampaignDiscountMedia));

    // select all unselected media types
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(RetailTmpCampaignDiscountMedia, IsSelected));
    queryBuildRange.value('(IsSelected == 0) || (CampaignMediaTypeId == \"' + this.text() + '\")');

    tableLookup.parmQuery(query);
    tableLookup.parmTmpBuffer(campaignMedia);
    tableLookup.setLabel("@SYS6303");

    tableLookup.performFormLookup();
}

