public class RetailCatalogProductAttributeValue extends FormRun implements RetailICatalogTranslation
    {
        FormDataSource      callerDataSource;
        CatalogProduct      callerCatalogProduct;
        EcoResProduct       callerProduct;
        boolean createInternalOrgAttributeGroup;
