public void close()
{
    if (!readOnlyMode)
    {
    retailCatalogProductAttributeFormHelper.saveProductAttributeValues();

    if (callerDataSource)
    {
        callerDataSource.research(true);
    }
    }

    super();
}

public RetailCatalogProductAttributeFormHelper getHelper()
{
    return retailCatalogProductAttributeFormHelper;
}

public void init()
{
    boolean validCaller;
    RetailInternalOrgRefRecId   internalOrgRefRecId;
    RetailInternalOrgProductAssortedDisplay internalOrgProductAssortedDisplay;

    FormRun callerFormRun = element.args().caller();

    if (callerFormRun ||
        element.args().dataset())
    {
        if (element.args().dataset() == tableNum(CatalogProduct))
        {
            validCaller = true;

            callerCatalogProduct = element.args().record();
            callerProduct = EcoResProduct::find(callerCatalogProduct.Product);

            Catalog catalogTable = Catalog::find(callerCatalogProduct.Catalog);
            if (catalogTable)
            {
                catalog = catalogTable.RecId;
            }
        }
        else if (element.args().dataset() == tableNum(RetailInternalOrgProductAssortedDisplay))
        {
            validCaller = true;

            internalOrgProductAssortedDisplay = element.args().record();
            callerDataSource = FormDataUtil::getFormDataSource(internalOrgProductAssortedDisplay);
            callerProduct = EcoResProduct::find(internalOrgProductAssortedDisplay.Product);
            internalOrgRefRecId = str2int64(element.args().parm());
            internalOrgRecId = internalOrgRefRecId;

            RetailInternalOrganization internalOrg = RetailInternalOrganization::find(internalOrgRecId);
        }
    }

    if (!validCaller)
    {
        throw error(strfmt("@SYS22678",element.name()));
    }

    sharedParams = RetailSharedParameters::find();

    super();

    // Disabling editing if caller was in View mode
    if (callerFormRun.inViewMode())
    {
        readOnlyMode = true;
        element.design().viewEditMode(ViewEditMode::View);
    }

    if (callerCatalogProduct)
    {
        retailCatalogProductAttributeFormHelper = RetailCatalogProductAttributeFormHelper::newFromCatalogProduct(
            element, callerCatalogProduct, retailTmpCatalogProductAttributes, readOnlyMode);
    }
    else
    {
        retailCatalogProductAttributeFormHelper = RetailCatalogProductAttributeFormHelper::newFromInternalOrgProduct(
            element, internalOrgRefRecId, callerProduct, retailTmpCatalogProductAttributes, readOnlyMode);
    }

    retailCatalogProductAttributeFormHelper.populateTempTable();
    retailCatalogProductAttributeFormHelper.addProductAttributeControls(SharedProductAttributesGroup, RetailProductAttributeRelationType::StandardProduct);
    retailCatalogProductAttributeFormHelper.addProductAttributeControls(SharedProductAttributesGroup, RetailProductAttributeRelationType::Category);

    if (!callerCatalogProduct)
    {
        internalOrganizationNameGrid.visible(false);
        retailCatalogProductAttributeFormHelper.addProductAttributeControls(ChannelProductAttributeGroup, RetailProductAttributeRelationType::Channel, internalOrgRefRecId);
    }
}

/// <summary>
/// This function needs to call when rich media (Image/Video) value has changed on <c>RetailRichMediaAttributeValue</c> form.
/// </summary>
public void onRichMediaChange()
{
    this.onSubAttributeChanged();
}

/// <summary>
/// This function needs to call when html value has changed on <c>RetailRichTextAttributeValue</c> form.
/// </summary>
public void onHtmlChanged()
{
    this.onSubAttributeChanged();
}

/// <summary>
/// This function needs to call when rich media (Image/Video) value / html value or text translate has changed on <c>RetailRichMediaAttributeValue</c>, <c>RetailRichTextAttributeValue</c> and <c>RetailCatalogProductAttributeTranslation</c> form.
/// </summary>
private void onSubAttributeChanged()
{
    if (callerCatalogProduct.RecId)
    {
        RetailCatalogProductStatus::setHasUnpublishedChange(callerCatalogProduct.RecId, NoYes::Yes);
    }
}

/// <summary>
/// This function needs to call when text translate has changed on <c>RetailCatalogProductAttributeTranslation</c> form.
/// </summary>
public void onTextTranslateChanged()
{
    this.onSubAttributeChanged();
}

