public display AttributeValueText value(RetailTmpCatalogProductAttributes _tmpAttribute)
{
    EcoResValue localEcoResValue = _tmpAttribute.joinChild().joinChild();
    if (retailCatalogProductAttributeFormHelper.useSnapshot())
    {
        return retailCatalogProductAttributeFormHelper.getSnapshotValue(_tmpAttribute.RecId);
    }
    return (localEcoResValue.RecId?localEcoResValue.value():_tmpAttribute.StandardAttributeTextValue);
}

