{
    super();

    if (callerCatalogProduct && retailCatalogInternalOrg && !channelProductAttributeGroup.controlCount())
    {
        retailCatalogProductAttributeFormHelper.addProductAttributeControls(ChannelProductAttributeGroup, RetailProductAttributeRelationType::Channel, retailCatalogInternalOrg.InternalOrganization);
    }
}

public int active()
{
    int ret;
    ret = super();

    if (callerCatalogProduct && createInternalOrgAttributeGroup && retailCatalogInternalOrg && channelProductAttributeGroup.controlCount())
    {
        retailCatalogProductAttributeFormHelper.updateInternalOrgProdAttributeGroupCtrl(this.formRun(), channelProductAttributeGroup, retailCatalogInternalOrg);
    }

    createInternalOrgAttributeGroup = true;

    return ret;
}

