/// <summary>
/// Method is used to open the <c>RetailInternalOrganization</c> form for InternalOrganization field.
/// </summary>
public void jumpRef()
{
    Args args = new Args();
    RetailInternalOrganization retailInternalOrganization;
    ;

    args.caller(this);
    args.openMode(OpenMode::View);
    retailInternalOrganization = RetailInternalOrganization::find(RetailCatalogInternalOrg.InternalOrganization);
    args.record(retailInternalOrganization);

    MenuFunction::runClient(menuitemDisplayStr(retailInternalOrganization), MenuItemType::Display, true, args);
}
