public void categoryAttributeChanged()
{
    EcoResTextValue enumerationValue;
    EcoResEnumerationAttributeTypeValue enumerationAttributeTypeValue;
    Query query_value = ecoResValue_Category_ds.query();
    QueryBuildDataSource qbds_categoryInstanceValue = query_value.dataSourceName('EcoResCategoryInstanceValue_Category');
    QueryBuildRange rangeCategory = qbds_categoryInstanceValue.findRange(fieldNum(EcoResCategoryInstanceValue, Category));
    QueryBuildDataSource qbds_categoryAttributeValue = query_value.dataSourceName('EcoResAttributeValue_Category');
    QueryBuildRange rangeAttribute = qbds_categoryAttributeValue.findRange(fieldNum(EcoResAttributeValue, Attribute));

    void refreshAttribute()
    {
        int currentPosition = attr_Cat_ds.getPosition();
        attr_Cat_ds.executeQuery();
        attr_Cat_ds.setPosition(currentPosition);
        ecoResValue_Category_ds.executeQuery();
    }

    if (!rangeCategory)
    {
        rangeCategory = qbds_categoryInstanceValue.addRange(fieldNum(EcoResCategoryInstanceValue, Category));
    }
    rangeCategory.value(queryValue(ecoResCategory.RecId));
    if (!rangeAttribute)
    {
        rangeAttribute = qbds_categoryAttributeValue.addRange(fieldNum(EcoResAttributeValue, Attribute));
    }
    rangeAttribute.value(queryValue(attr_Cat.RecId));

    refreshAttribute();

    if (attr_Cat.RecId && !ecoResValue_Category.RecId)
    {
        element.createCategoryValue();
    }

    refreshAttribute();
    if (AttributeDataTypeDiscriminator::checkAttributeDataTypeText(AttributeDataType::Text) && attrType_Cat.IsEnumeration)
    {
        // Populate enumeration combox box
        categoryEnumerationComboBox.clear();
        while select TextValue from enumerationValue
            exists join enumerationAttributeTypeValue
            where enumerationAttributeTypeValue.Value == enumerationValue.RecId
               && enumerationAttributeTypeValue.AttributeType == attrType_Cat.RecId
        {
            categoryEnumerationComboBox.add(enumerationValue.TextValue);
        }
        categoryEnumerationComboBox.selectText(ecoResValue_Category_EcoResTextValue.TextValue);
    }

    element.categoryValueSetInvisible();
    element.categoryValueSetVisible();
}

public void categoryAttributeEditButtonClicked()
{
    if (RetailAttributeHelper::isAttributeTypeHtml(attrType_Cat.RecId))
    {
        RetailAttributeHelper::editHtmlAttributeValue(element, ecoResValue_Category_EcoResTextValue);
    }
    else if (RetailAttributeHelper::isAttributeTypeImages(attrType_Cat.RecId))
    {
        RetailAttributeHelper::editMediaAttributeValue(element, ecoResValue_Category_EcoResTextValue, RetailRichMediaType::Images, RetailRichMediaHelper::getUrlTemplateMapByCategory(ecoResCategory));
    }
    else if (RetailAttributeHelper::isAttributeTypeVideos(attrType_Cat.RecId))
    {
        RetailAttributeHelper::editMediaAttributeValue(element, ecoResValue_Category_EcoResTextValue, RetailRichMediaType::Videos, RetailRichMediaHelper::getUrlTemplateMapByCategory(ecoResCategory));
    }
}

public void categoryEnumerationChanged()
{
    // Update text value
    ecoResValue_Category_EcoResTextValue.TextValue = categoryEnumerationComboBox.getText(categoryEnumerationComboBox.selection());
}

/// <summary>
/// Disable POSDisallowManualDiscount checkbox when POSDisallowDiscount is Yes.
/// POSDisallowManualDiscount is a subset option of POSDisallowDiscount. When POSDisallowDiscount is Yes, POSDisallowManualDiscount must match and should not be edited.
/// </summary>
public void enableManualDiscountCheckBox()
{
    boolean shouldEnableManualDiscountAllowedCheckBox = retailAttributesLegalEntityLookup.RetailPOSDisallowDiscount != NoYes::Yes;
    POSTerminals_RetailPOSDisallowManualDiscount.enabled(shouldEnableManualDiscountAllowedCheckBox);
}

private void categoryValueSetInvisible()
{
    categoryValueStringEdit.visible(false);
    categoryAttributeValueTranslateButton.enabled(false);
    categoryValueIntEdit.visible(false);
    categoryValueUtcDateTimeEdit.visible(false);
    categoryValueRealEdit.visible(false);
    categoryCurrencyGroup.visible(false);
    categoryCurrencyValueRealEdit.visible(false);
    categoryCurrencyCode.visible(false);
    categoryValueBooleanEdit.visible(false);
    categoryEnumerationComboBox.visible(false);
    categoryFloatUnitOfMeasure.visible(false);
    categoryIntUnitOfMeasure.visible(false);
    categoryIntegerDecimalGroup.visible(false);
    categoryAttributeEditButton.visible(false);
}

    public void categoryValueSetVisible()
    {
        if (ecoResValue_Category)
        {
            switch (ecoResValue_Category.getInstanceRelationType())
            {
                case tableStr(EcoResBooleanValue):
                    categoryValueBooleanEdit.visible(true);
                    break;
                case tableStr(EcoResDateTimeValue):
                    categoryValueUtcDateTimeEdit.visible(true);
                    break;
                case tableStr(EcoResFloatValue):
                    categoryValueRealEdit.visible(true);
                    categoryIntegerDecimalGroup.visible(true);
                    if (EcoResAttributeTypeUnitOfMeasure::findByAttributeType(attrType_Cat).RecId)
                    {
                        categoryFloatUnitOfMeasure.visible(true);
                    }
                    break;
                case tableStr(EcoResCurrencyValue):
                    categoryCurrencyValueRealEdit.visible(true);
                    categoryCurrencyCode.visible(true);
                    categoryCurrencyGroup.visible(true);
                    break;
                case tableStr(EcoResIntValue):
                    categoryValueIntEdit.visible(true);
                    categoryIntegerDecimalGroup.visible(true);
                    if (EcoResAttributeTypeUnitOfMeasure::findByAttributeType(attrType_Cat).RecId)
                    {
                        categoryIntUnitOfMeasure.visible(true);
                    }
                    break;
                case tableStr(EcoResTextValue):

                    if (RetailAttributeHelper::isRichAttributeType(attrType_Cat.RecId))
                    {
                        categoryAttributeEditButton.visible(true);
                        categoryAttributeValueTranslateButton.enabled(true);
                    }
                    else if (attrType_Cat.IsEnumeration == NoYes::Yes)
                    {
                        categoryEnumerationComboBox.visible(true);
                    }
                    else
                    {
                        categoryAttributeValueTranslateButton.enabled(true);
                        categoryValueStringEdit.visible(true);
                    }
                    break;
                default:
					// Unsupported
            }
        }
    }

public void cleanCategoryBackupProperties(RefRecId _categoryId)
{
    if (mOriginalCategoryProperties.exists(_categoryId))
    {
        mOriginalCategoryProperties.remove(_categoryId);
        this.refreshStaticAttributes();
    }
}

public void close()
{
    super();

    // make sure category containment is synchronized
    if (this.getHierarchyRole() == EcoResCategoryNamedHierarchyRole::RetailSpecialGroup)
    {
        RetailCategoryContainmentLookup::synchronizeCategoriesLookup();
    }
}

public void createCategoryValue()
{
    RefRecId categoryInstanceRecId;
    EcoResAttributeValue attributeValue;
    EcoResAttributeGroupAttribute attributeGroupAttribute;
    EcoResCategoryAttributeGroup ecoResCategoryAttributeGroup;
    EcoResCategory category, nearestAncestorWithNoInheritance;

    int currentPosition = attr_Cat_ds.getPosition();

    if (ecoResCategory.RecId)
    {
        categoryInstanceRecId = this.getCategoryInstance();

        // select the first attribute group containing the attribute to display the value control for,
        // which is associated to the current category at maximum level.
        if (ecoResCategory.IsCategoryAttributesInherited == NoYes::No)
        {
            select firstonly attributeGroupAttribute
                where attributeGroupAttribute.Attribute == attrGrpAttr_Cat.Attribute
            exists join ecoResCategoryAttributeGroup
                where ecoResCategoryAttributeGroup.AttributeGroup == attributeGroupAttribute.AttributeGroup
                  && ecoResCategoryAttributeGroup.Category == ecoResCategory.RecId
                  && ecoResCategoryAttributeGroup.Modifier == EcoResCategoryAttributeModifier::Category;
        }
        else
        {
            nearestAncestorWithNoInheritance = EcoResCategory::getNearestAncestorWithNoInheritance(ecoResCategory);

            select firstonly attributeGroupAttribute
                where attributeGroupAttribute.Attribute == attrGrpAttr_Cat.Attribute
            exists join ecoResCategoryAttributeGroup
                where ecoResCategoryAttributeGroup.AttributeGroup == attributeGroupAttribute.AttributeGroup
                   && ecoResCategoryAttributeGroup.Modifier == EcoResCategoryAttributeModifier::Category
            exists join category
                where category.RecId == ecoResCategoryAttributeGroup.Category
                   && category.CategoryHierarchy == ecoResCategory.CategoryHierarchy
                   && category.NestedSetLeft >= nearestAncestorWithNoInheritance.NestedSetLeft
                   && category.NestedSetLeft <= ecoResCategory.NestedSetLeft
                   && category.NestedSetRight >= ecoResCategory.NestedSetRight
                   && category.NestedSetRight <= nearestAncestorWithNoInheritance.NestedSetRight
                   && category.Level == ecoResCategory_CategoryAttributes.Level;
        }

        attributeValue =
            EcoResAttributeValue::findOrCreateDefault(categoryInstanceRecId, attrGrpAttr_Cat.Attribute, attributeGroupAttribute);
    }

    attr_Cat_ds.executeQuery();
    attr_Cat_ds.setPosition(currentPosition);
}

private void createLegalEntityLookups(RefRecId _categoryRecId)
{
    EcoResCategoryInstanceValue ecoResCategoryInstanceValue, ecoResCategoryInstanceValueCheck;
    RetailAttributesLegalEntityLookup attributeLookup, attributeLookupCheck;
    CompanyInfo legalEntity;

    insert_recordset ecoResCategoryInstanceValue (Category, BuyingLegalEntity)
        select _categoryRecId, RecId from legalEntity
        notexists join ecoResCategoryInstanceValueCheck
        where ecoResCategoryInstanceValueCheck.Category == _categoryRecId
           && ecoResCategoryInstanceValueCheck.BuyingLegalEntity == legalEntity.RecId;

    insert_recordset attributeLookup (EcoResInstanceValue, LegalEntity, Category)
        select RecId, BuyingLegalEntity, Category from ecoResCategoryInstanceValue
        where ecoResCategoryInstanceValue.Category == _categoryRecId
           && ecoResCategoryInstanceValue.BuyingLegalEntity
        notExists join attributeLookupCheck
            where attributeLookupCheck.EcoResInstanceValue == ecoResCategoryInstanceValue.RecId;
}

public EcoResTextValue getCategoryAttributeValue(RecId _attributeTypeRecId)
{
    return EcoResTextValue::find(
        RetailAttributeHelper::findOrCreateAttributeValue(
            this.getCategoryInstance(),
            sharedParams.CategoryDefaultAttributeGroup,
            _attributeTypeRecId).RecId, true);
}

