{
    return mCategoryHierarchy;
}

private RefRecId getCategoryInstance()
{
    return EcoResCategoryInstanceValue::insertCategoryInstance(ecoResCategory.RecId, 0 /* global instance */);
}

public EcoResCategoryNamedHierarchyRole getHierarchyRole()
{
    return mHierarchyRole;
}

private EcoResCategoryHierarchy getRetailProductHierarchy()
{
    return EcoResCategoryHierarchy::find(
        EcoResCategoryHierarchyRole::getHierarchiesByRole(
            EcoResCategoryNamedHierarchyRole::Retail).CategoryHierarchy);
}

public str getSetGroupForPropertyFilterValue(int _filterValue, str _groupName = '')
{
    if (!mPropertyFilter2GroupMap)
    {
        mPropertyFilter2GroupMap = new Map(Types::Integer, Types::String);
    }

    if (_groupName)
    {
        mPropertyFilter2GroupMap.insert(_filterValue, _groupName);
    }

    if (mPropertyFilter2GroupMap.exists(_filterValue))
    {
        return mPropertyFilter2GroupMap.lookup(_filterValue);
    }
    else
    {
        return '';
    }
}

