public void init()
{
    Name DOMManageRulesFormName = 'DOMManageRules';

    super();

    EcoResCategory category = element.args().lookupRecord();

    Attr_Cat_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResAttribute, Partition));
    Attr_Cat_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResAttribute, RecId));
    Attr_Cat_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResAttribute, Name));
    Attr_Cat_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResAttribute, AttributeType));
    AttrGrpAttr_Cat_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResAttributeGroupAttribute, Attribute));
    AttrType_Cat_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResAttributeType, Name));
    AttrType_Cat_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResAttributeType, RecId));
    AttrType_Cat_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResAttributeType, IsEnumeration));
    AttrType_Cat_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResAttributeType, DataType));
    EcoResCategory_CategoryAttributes_DS.queryBuildDataSource().addGroupByField(fieldnum(EcoResCategory, Level));

    mOriginalCategoryProperties = new Map(Types::Int64, Types::Class);
    mCategoryLevelMap = new Map(Types::Int64, Types::String);

    sharedParams = RetailSharedParameters::find();

    this.initializeCategoryHierarchy();
    this.initializeFormElements();

    // If the caller is <c>DomManageRules</c> form or <c>RetailCommerceValidatorResultsWorkSpace</c> open the selected hierarchy,
    // otherwise select the full hierarchy tree node.
    if (category.RecId && element.args().caller() 
        && ( element.args().caller().name() == DOMManageRulesFormName
        ||   element.args().caller().name() == formStr(RetailCommerceValidatorResultsWorkSpace))
        )
    {
        this.initializeCategoryTree(category.RecId);
    }
    else
    {
        this.initializeCategoryTree();
    }

    // register click event for all ToggleViewMode buttons
    retailToggleViewMode.OnClicked += eventhandler(this.retailToggleViewMode);
    generalToggleViewMode.OnClicked += eventhandler(this.generalToggleViewMode);
    purchaseToggleViewMode.OnClicked += eventhandler(this.purchaseToggleViewMode);
    salesToggleViewMode.OnClicked += eventhandler(this.salesToggleViewMode);
    inventoryToggleViewMode.OnClicked += eventhandler(this.inventoryToggleViewMode);
    costToggleViewMode.OnClicked += eventhandler(this.costToggleViewMode);
    basicToggleViewMode.OnClicked += eventhandler(this.basicToggleViewMode);
    inventoryCategoryToggleViewMode.OnClicked += eventhandler(this.inventoryCategoryToggleViewMode);

    if (FeatureStateProvider::isFeatureEnabled(RetailEnableDisplayOrderForMerchandisingFeature::instance()))
    {
        // Set visible to true when feature is Enabled, by default, the feature is Disabled and visible is false.
        ecoResCategory_DisplayOrder.visible(true);
        ecoResProductCategory_DisplayOrder.visible(true);
        EcoResAttributeGroup_Product_DisplayOrder.visible(true);
        EcoResProductAttributeGroup_ds.queryBuildDataSource().addSortField(fieldNum(EcoResCategoryAttributeGroup, DisplayOrder), SortOrder::Ascending);
        EcoResProductAttributeGroup_ds.queryBuildDataSource().addSortField(fieldNum(EcoResCategoryAttributeGroup, AttributeGroup), SortOrder::Ascending);
    }

    RetailAttributesLegalEntityLookup_ds.object(fieldNum(RetailAttributesLegalEntityLookup, SalesRetailInventoryAvailabilityBuffer)).visible(true);
    RetailAttributesLegalEntityLookup_ds.object(fieldNum(RetailAttributesLegalEntityLookup, SalesRetailInventoryAvailabilityLevelProfile)).visible(true);

    if (RetailMerchandisingKillSwitch::enableCategoryAttributesAndCategoryAttributeGroups())
    {
        categoryAttributes.visible(true);
    }
    else
    {
        categoryAttributes.visible(false);
        EcoResValue_Category_DS.queryBuildDataSource().enabled(false);
    }
}

private void initializeCategoryHierarchy()
{
    SysDictEnum sysDictEnum = new SysDictEnum(enumNum(EcoResCategoryNamedHierarchyRole));
    int hierarchyRole;
    Args args = element.args();

    // use passed in hierarchy if exists
    if (element.args() &&
        element.args().dataset() == tableNum(EcoResCategoryHierarchy))
    {
        mCategoryHierarchy = element.args().record();
    }
    // otherwise default to retail hierarchy
    else
    {
        mCategoryHierarchy = this.getRetailProductHierarchy();
    }

    // if still no hierarchy, fail with standard missing buffer exception
    if (!mCategoryHierarchy)
    {
        throw error("@RET5777");
    }

    if (args && args.parm())
    {
        hierarchyRole = sysDictEnum.symbol2Value(args.parm()); // returns 255 if the value is not a valid member if the enum
        if (hierarchyRole != 255)
        {
            mHierarchyRole = any2Enum(hierarchyRole);
        }
    }

    if (!mHierarchyRole)
    {
        // find role from hierarchy
        if (EcoResCategoryHierarchyRole::existRoleHierarchyPair(
                    EcoResCategoryNamedHierarchyRole::Retail,
                    mCategoryHierarchy.RecId))
        {
            mHierarchyRole = EcoResCategoryNamedHierarchyRole::Retail;
        }
        else if (EcoResCategoryHierarchyRole::existRoleHierarchyPair(
                    EcoResCategoryNamedHierarchyRole::RetailSpecialGroup,
                    mCategoryHierarchy.RecId))
        {
            mHierarchyRole = EcoResCategoryNamedHierarchyRole::RetailSpecialGroup;
            this.design().caption("@RET260229");
        }
        else if (EcoResCategoryHierarchyRole::existRoleHierarchyPair(
                    EcoResCategoryNamedHierarchyRole::RetailChannelNavigation,
                    mCategoryHierarchy.RecId))
        {
            this.design().caption("@RET15091");
            mHierarchyRole = EcoResCategoryNamedHierarchyRole::RetailChannelNavigation;
        }
    }

    if (mHierarchyRole != EcoResCategoryNamedHierarchyRole::Retail &&
        mHierarchyRole != EcoResCategoryNamedHierarchyRole::RetailSpecialGroup &&
        mHierarchyRole != EcoResCategoryNamedHierarchyRole::RetailChannelNavigation)
    {
        error(strFmt("@RET15092", enum2str(mHierarchyRole)));
    }
}

private void initializeCategoryTree(RefRecId _categoryRecId = 0)
{
    EcoResCategoryLookupParameters lookupParameters = new EcoResCategoryLookupParameters(false, false, false);
    EcoResCategoryHierarchy retailCategoryHierarchy = this.getCategoryHierarchy();
    EcoResCategory root;

    if (_categoryRecId)
    {
        root = EcoResCategory::find(_categoryRecId);
    }
    else
    {
        root = EcoResCategory::getRoot(retailCategoryHierarchy.RecId);
    }

    mHierarchyTree = new EcoResCategoryTreeDatasource( ecoResCategory_ds,
                                                       hierarchyTreeCtrl,
                                                       fieldNum(EcoResCategory, RecId),
                                                       fieldNum(EcoResCategory, ParentCategory),
                                                       fieldNum(EcoResCategory, Name),
                                                       false /* No ID in description */,
                                                       true,
                                                       fieldNum(EcoResCategory, Code),
                                                       retailCategoryHierarchy,
                                                       null,
                                                       lookupParameters,
                                                       fieldNum(EcoResCategory, NestedSetLeft) /* for tree sort order */
                                                       );

    mHierarchyTree.initRoot(root.Name, root.RecId, mHierarchyTree.image() );
}

/// <summary>
/// Initializes the form element with input data.
/// </summary>
protected void initializeFormElements()
{
    if (this.getHierarchyRole() == EcoResCategoryNamedHierarchyRole::Retail)
    {
        retailProductProperties.enabled(true);
        retailProductProperties.visible(true);
        generalProductProperties.enabled(true);
        generalProductProperties.visible(true);
        purchaseProductProperties.enabled(true);
        purchaseProductProperties.visible(true);
        salesProductProperties.enabled(true);
        salesProductProperties.visible(true);
        inventoryProductProperties.enabled(true);
        inventoryProductProperties.visible(true);
        costProductProperties.enabled(true);
        costProductProperties.visible(true);
        basicProductProperties.enabled(true);
        basicProductProperties.visible(true);
        inventoryCategoryProperties.enabled(true);
        inventoryCategoryProperties.visible(true);

        // Info code fast tab will be visible on Retail Category Hierarchy only
        infoCodeAttributes.visible(true);
        updateProductsButton.enabled(true);
        updateProductsButton.visible(true);
        this.initializePropertyGroupFilter();

        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            FiscalInformation_BR.visible(true);
        }
        // </GBR>

        // <GEERU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            Return_RU.visible(true);
            labelAttributes.visible(true);
        }
        // </GEERU>
    }
    else if (this.getHierarchyRole() == EcoResCategoryNamedHierarchyRole::RetailSpecialGroup)
    {
        specialCategoryProducts.enabled(true);
        specialCategoryProducts.visible(true);
        products.visible(false);
        products.enabled(false);
    }
    else if (this.getHierarchyRole() == EcoResCategoryNamedHierarchyRole::RetailChannelNavigation)
    {
        retailProductProperties.enabled(false);
        retailProductProperties.visible(false);
        basicProductProperties.enabled(false);
        basicProductProperties.visible(false);

        // Info code fast tab visible set to 'false' for other category hierarchy
        infoCodeAttributes.visible(false);
        updateProductsButton.enabled(false);
        updateProductsButton.visible(false);
        attributeGroupTypeCB.visible(true);
        this.initializePropertyGroupFilter();
    }
}

/// <summary>
/// Initializes the form property group filters with input data.
/// </summary>
protected void initializePropertyGroupFilter()
{
    DictTable dictTable = new DictTable(tableNum(RetailAttributesLegalEntityLookup));
    DictFieldGroup fieldGroup;
    str groupLabel;
    str groupName;
    int i;
    //<GEERU>
    boolean isCountryRegionRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    //</GEERU>

    for (i=1; i<=dictTable.fieldGroupCnt(); i++)
    {
        fieldGroup = new DictFieldGroup(dictTable.id(), dictTable.fieldGroup(i));

        //<GEERU>
        if (isCountryRegionRU && fieldGroup.name() == labelAttributes.dataGroup())
        {
            continue;
        }
        //</GEERU>

        // seperate groups by group name prefix
        groupLabel = fieldGroup.label();
        groupName = fieldGroup.name();
        if (strScan(groupName, 'Purchase', 1, strLen('Purchase')))
        {
            purchasePropertyGroupFilter.add(groupLabel);
            element.getSetGroupForPurchasePropertyFilterValue(
                purchasePropertyGroupFilter.items() - 1,
                groupName);
        }
        else if (strScan(groupName, 'Sales', 1, strLen('Sales')))
        {
            salesPropertyGroupFilter.add(groupLabel);
            element.getSetGroupForSalesPropertyFilterValue(
                salesPropertyGroupFilter.items() - 1,
                groupName);
        }
        else if (strScan(groupName, 'Inventory', 1, strLen('Inventory')))
        {
            inventoryPropertyGroupFilter.add(groupLabel);
            element.getSetGroupForInventoryPropertyFilterValue(
                inventoryPropertyGroupFilter.items() - 1,
                groupName);
        }
        else if (strScan(groupName, 'Cost', 1, strLen('Cost')))
        {
            costPropertyGroupFilter.add(groupLabel);
            element.getSetGroupForCostPropertyFilterValue(
                costPropertyGroupFilter.items() - 1,
                groupName);
        }
        else if (strScan(groupName, 'Retail', 1, strLen('Retail')))
        {
            retailPropertyGroupFilter.add(groupLabel);
            element.getSetGroupForRetailPropertyFilterValue(
                retailPropertyGroupFilter.items() - 1,
                groupName);
        }
        else if (strScan(groupName, 'General', 1, strLen('General')))
        {
            generalPropertyGroupFilter.add(groupLabel);
            element.getSetGroupForGeneralPropertyFilterValue(
                generalPropertyGroupFilter.items() - 1,
                groupName);
        }
        else if (strScan(groupName, 'ProductCategory', 1, strLen('ProductCategory')))
        {
            categoryPropertyGroupFilter.add(groupLabel);
            element.getSetGroupForCategoryPropertyFilterValue(
                categoryPropertyGroupFilter.items() - 1,
                groupName);
        }
        // the rest groups go to basic product properties fast tab
        else if (groupLabel)
        {
            propertyGroupFilter.add(groupLabel);
            element.getSetGroupForPropertyFilterValue(
                propertyGroupFilter.items() - 1,
                fieldGroup.name());
        }
    }

    if (propertyGroupFilter.count() > 0)
    {
        this.refreshPropertyGridGroups(propertyGroupFilter.selection());
    }

    if (purchasePropertyGroupFilter.count() > 0)
    {
        this.refreshPurchasePropertyGridGroups(purchasePropertyGroupFilter.selection());
    }

    if (salesPropertyGroupFilter.count() > 0)
    {
        this.refreshSalesPropertyGridGroups(salesPropertyGroupFilter.selection());
    }

    if (inventoryPropertyGroupFilter.count() > 0)
    {
        this.refreshInventoryPropertyGridGroups(inventoryPropertyGroupFilter.selection());
    }

    if (costPropertyGroupFilter.count() > 0)
    {
        this.refreshCostPropertyGridGroups(costPropertyGroupFilter.selection());
    }

    if (retailPropertyGroupFilter.count() > 0)
    {
        this.refreshRetailPropertyGridGroups(retailPropertyGroupFilter.selection());
    }

    if (generalPropertyGroupFilter.count() > 0)
    {
        this.refreshGeneralPropertyGridGroups(generalPropertyGroupFilter.selection());
    }

    if (categoryPropertyGroupFilter.count() > 0)
    {
        this.refreshCategoryPropertyGridGroups(categoryPropertyGroupFilter.selection());
    }
}

public void refreshCategoryAttributes()
{
    catyAttrGrp_ds.query().dataSourceTable(tableNum(EcoResCategoryAttributeGroup))
        .clearRange(fieldNum(EcoResCategoryAttributeGroup,Category));

    ecoResCategory_CategoryAttributes_ds.query().dataSourceTable(tableNum(EcoResCategory))
        .clearRanges();

    EcoResCategory::setCategoryRangeBasedOnInheritance(
        ecoResCategory_CategoryAttributes_ds,
        ecoResCategory,
        fieldNum(EcoResCategory, IsCategoryAttributesInherited));

    catyAttrGrp_ds.executeQuery();

    // hide polymorphic attribute value group if no attribute selected
    categoryAttributeValueGroup.visible(attr_Cat.RecId != 0);
}

/// <summary>
/// Updates the category level map for the currently selected category.
/// </summary>
/// <remarks>
/// The map stores the category names for all the categories in the path from the root category to the currenly
/// selected node based on its level.
/// Level 1 represents the root category, level 2 its child on the path to the currentsly selected node, etc.
/// </remarks>
public void refreshCategoryLevelMap()
{
    EcoResCategory category = ecoResCategory;

    while (category)
    {
        mCategoryLevelMap.insert(category.Level, category.Name);
        category = category.getParent();
    }
}

public void refreshCategoryProducts()
{
    if (this.getHierarchyRole() == EcoResCategoryNamedHierarchyRole::RetailSpecialGroup)
    {
        retailSpecialCategoryMember_ds.executeQuery();
    }
    else if (this.getHierarchyRole() == EcoResCategoryNamedHierarchyRole::Retail ||
             this.getHierarchyRole() == EcoResCategoryNamedHierarchyRole::RetailChannelNavigation)
    {
        ecoResProductCategory_ds.executeQuery();
    }
}

public void refreshCategoryVendor()
{
    retailCategoryVendCategoryMapping_ds.query().dataSourceNo(1)
        .clearRange(fieldNum(RetailCategoryVendCategoryMapping,RetailCategoryId));

    retailCategoryVendCategoryMapping_ds.query().dataSourceNo(1)
        .addRange(fieldNum(RetailCategoryVendCategoryMapping,RetailCategoryId))
        .value(queryValue(ecoResCategory.RecId));

    retailCategoryVendCategoryMapping_ds.executeQuery();
}

public void refreshProductAttributes()
{
