        ecoResCategory_ProductAttributes_ds,
        ecoResCategory,
        fieldNum(ProcCategoryModifier, IsProductAttributesInherited));

    ecoResProductAttributeGroup_ds.executeQuery();
}

public void refreshPropertyGridGroups(int _propertyFilterValue)
{
    List gridGroupControls = new List(Types::Class);
    ListEnumerator controls;
    FormGroupControl groupControl;
    str currentGroup;

    gridGroupControls.addStart(gridUnits);
    gridGroupControls.addStart(gridGroups);

    if (isConfigurationkeyEnabled(configurationKeyNum(WHSandTMS)))
    {
        gridGroupControls.addStart(gridWarehouseManagement);
    }

    gridGroupControls.addStart(gridDefaultWarehouses);
    gridGroupControls.addStart(gridDefaultReports);
    gridGroupControls.addStart(gridLabelAttributes);

    controls = gridGroupControls.getEnumerator();

    currentGroup = element.getSetGroupForPropertyFilterValue(_propertyFilterValue);

    while (controls.moveNext())
    {
        groupControl = controls.current();
        groupControl.visible(
            groupControl.dataGroup() == currentGroup);
    }
}

