public void refreshStaticAttributes()
{
    RetailProductAttributesReference originalAttributes;

    if (!ecoResCategory)
    {
        // there is no current category selected.
        return;
    }

    if (!mOriginalCategoryProperties.exists(ecoResCategory.RecId))
    {
        originalAttributes = RetailProductAttributesReference::construct();
        originalAttributes.unpack(RetailProductAttributesReference::constructOnServer(ecoResCategory.RecId));
        mOriginalCategoryProperties.insert(ecoResCategory.RecId, originalAttributes);
    }

    // filter global retail attributes
    retailAttributesGlobalLookup_ds.query()
        .dataSourceTable(tableNum(RetailAttributesGlobalLookup))
        .clearRange(fieldNum(RetailAttributesGlobalLookup, Category));

    retailAttributesGlobalLookup_ds.query()
        .dataSourceTable(tableNum(RetailAttributesGlobalLookup))
        .addRange(fieldNum(RetailAttributesGlobalLookup, Category))
        .value(queryValue(ecoResCategory.RecId));

    retailAttributesGlobalLookup_ds.executeQuery();
    if (!retailAttributesGlobalLookup.RecId)
    {
        retailAttributesGlobalLookup.EcoResInstanceValue = EcoResCategoryInstanceValue::insertCategoryInstance(ecoResCategory.RecId, 0);
        retailAttributesGlobalLookup.Category = ecoResCategory.RecId;
        retailAttributesGlobalLookup.LegalEntity = CompanyInfo::current();
        retailAttributesGlobalLookup.IsSynchronized = NoYes::Yes;
        retailAttributesGlobalLookup.insert();
        retailAttributesGlobalLookup_ds.executeQuery();
    }
    this.createLegalEntityLookups(ecoResCategory.RecId);

    if (!retailAttributesGlobalLookup.RecId)
    {
        retailAttributesGlobalLookup.EcoResInstanceValue = EcoResCategoryInstanceValue::insertCategoryInstance(ecoResCategory.RecId, 0);
        retailAttributesGlobalLookup.Category = ecoResCategory.RecId;
        retailAttributesGlobalLookup.LegalEntity = CompanyInfo::current();
        retailAttributesGlobalLookup.IsSynchronized = NoYes::Yes;
        retailAttributesGlobalLookup.insert();
        retailAttributesGlobalLookup_ds.executeQuery();
    }

    this.createLegalEntityLookups(ecoResCategory.RecId);

    // filter LE-specific retail attributes
    retailAttributesLegalEntityLookup_ds.query()
        .dataSourceTable(tableNum(EcoResCategoryInstanceValue))
        .clearRange(fieldNum(EcoResCategoryInstanceValue, Category));

    retailAttributesLegalEntityLookup_ds.query()
        .dataSourceTable(tableNum(EcoResCategoryInstanceValue))
        .addRange(fieldNum(EcoResCategoryInstanceValue, Category))
        .value(queryValue(ecoResCategory.RecId));

    retailAttributesLegalEntityLookup_ds.executeQuery();
}

/// <summary>
/// Refresh info code grid based on selected category hierarchy and legal entity.
/// </summary>
public void refreshCategoryInfoCode()
{
    // Clear any previously added ranges
    retailInfocodeCategorySpecific_q.dataSourceTable(tableNum(RetailInfocodeCategorySpecific)).clearRanges();

    // Add category name and hierarchy filters
    retailInfocodeCategorySpecific_q.dataSourceTable(tableNum(RetailInfocodeCategorySpecific))
        .addRange(fieldNum(RetailInfocodeCategorySpecific, CategoryHierarchy)).value(queryValue(ecoResCategory.CategoryHierarchy));
    retailInfocodeCategorySpecific_q.dataSourceTable(tableNum(RetailInfocodeCategorySpecific))
        .addRange(fieldNum(RetailInfocodeCategorySpecific, CategoryName)).value(queryValue(ecoResCategory.Name));

    // Hide category hierarchy and name fields.
    retailInfocodeCategorySpecific_ds.object(fieldnum(RetailInfocodeCategorySpecific, CategoryHierarchy)).visible(false);
    retailInfocodeCategorySpecific_ds.object(fieldnum(RetailInfocodeCategorySpecific, CategoryName)).visible(false);

    // Filter records for selected LE
    retailInfocodeCategorySpecific_ds.query().clearCompanyRange();
    retailInfocodeCategorySpecific_ds.query().addCompanyRange(companyInfo.DataArea);
    retailInfocodeCategorySpecific_ds.executeQuery();
}

