public void run()
{
    super();

    // Make current legal entity selected in legal entities lookup
    LELookupStringEdit.text(strUpr(curExt()));
    LELookupStringEdit.modified();
}

public int task(int _taskId)
{
    int ret;
    #Task

    deletingRecord = (_taskId == #taskDeleteRecord);

    if (retailGroupMemberLine && _taskId == #taskDeleteRecord)
    {
        retailGroupMemberLine_ds.write();
    }

    ret = super(_taskId);

    if (_taskId == #taskF5)
    {
        this.initializeCategoryTree();
    }

    // when view mode changes to view/edit, set the ViewMode buttons
    if (_taskId == #taskEditRecord || _taskId == #taskDoneEditRecord)
    {
        this.setViewModeButtonText(retailToggleViewMode, retailLegalEntityView);
        this.setViewModeButtonText(generalToggleViewMode, generalLegalEntityView);
        this.setViewModeButtonText(purchaseToggleViewMode, purchaseLegalEntityView);
        this.setViewModeButtonText(salesToggleViewMode, salesLegalEntityView);
        this.setViewModeButtonText(inventoryToggleViewMode, inventoryLegalEntityView);
        this.setViewModeButtonText(costToggleViewMode, costLegalEntityView);
        this.setViewModeButtonText(basicToggleViewMode, basicLegalEntityView);
        this.setViewModeButtonText(inventoryCategoryToggleViewMode, categoryLegalEntityView);
    }

    return ret;
}

private void updateSelectedDataAreaWithValue()
{
}

public void addAttributeGroupsFormClosing()
{
    EcoResProductAttributeGroup_ds.executeQuery();
}

/// <summary>
/// Enables or disables the product dimension fields for alternative item.
/// </summary>
void setAltItemProductDimensionFields()
{
    boolean     altConfigActive;
    boolean     altSizeActive;
    boolean     altColorActive;
    boolean     altStyleActive;
    boolean     altVersionActive;
    InventTable altInventTable;

    altInventTable      = InventTable::find(RetailAttributesLegalEntityLookup.SalesAltItemId);
    altConfigActive     = altInventTable.RecId && altInventTable.configActive();
    altSizeActive       = altInventTable.RecId && altInventTable.sizeActive();
    altColorActive      = altInventTable.RecId && altInventTable.colorActive();
    altStyleActive      = altInventTable.RecId && altInventTable.styleActive();
    altVersionActive    = altInventTable.RecId && altInventTable.productVersionActive();

    retailAttributesLegalEntityLookup_ds.object(fieldNum(RetailAttributesLegalEntityLookup, SalesAltConfigId)).allowEdit(altConfigActive);
    retailAttributesLegalEntityLookup_ds.object(fieldNum(RetailAttributesLegalEntityLookup, SalesAltInventSizeId)).allowEdit(altSizeActive);
    retailAttributesLegalEntityLookup_ds.object(fieldNum(RetailAttributesLegalEntityLookup, SalesAltInventColorId)).allowEdit(altColorActive);
    retailAttributesLegalEntityLookup_ds.object(fieldNum(RetailAttributesLegalEntityLookup, SalesAltInventStyleId)).allowEdit(altStyleActive);
    retailAttributesLegalEntityLookup_ds.object(fieldNum(RetailAttributesLegalEntityLookup, SalesAltInventVersionId)).allowEdit(altVersionActive);
}

