private void setViewModeButtonText(FormButtonControl button, FormGroupControl LETabPage)
{
    if (this.inViewMode())
    {
        if (LETabPage.visible())
        {
            button.text("@Retail:ViewInGrid");
        }
        else
        {
            button.text("@Retail:ViewByLegalEntity");
        }
    }
    else
    {
        if (LETabPage.visible())
        {
            button.text("@Retail:EditInGrid");
        }
        else
        {
            button.text("@Retail:EditByLegalEntity");
        }
    }
}

/// <summary>
/// Performs a lookup on the company info.
/// </summary>
/// <param name = "_control">An instance of <c>FormStringControl</c>.</param>
protected void LELookup(FormStringControl _control)
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(CompanyInfo), _control);

    sysTableLookup.addLookupField(fieldNum(CompanyInfo, DataArea));

    queryBuildDataSource = query.addDataSource(tableNum(CompanyInfo));
    queryBuildDataSource.addOrderByField(fieldNum(CompanyInfo, DataArea));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

private void LEModified(FormStringControl _control)
{
    select firstonly selectedDataArea
        where selectedDataArea.LegalEntity == CompanyInfo::findDataArea(_control.text()).RecId &&
            selectedDataArea.Category == ecoResCategory.RecId;

    RetailAttributesLegalEntityLookup_ds.positionToRecord(selectedDataArea);

    LELookupStringEdit.text(_control.text());
    purchaseLELookupStringEdit.text(_control.text());
    salesLELookupStringEdit.text(_control.text());
    inventoryLELookupStringEdit.text(_control.text());
    costLELookupStringEdit.text(_control.text());
    generalLELookupStringEdit.text(_control.text());
    retailLELookupStringEdit.text(_control.text());
    categoryLELookupStringEdit.text(_control.text());

    element.setAltItemProductDimensionFields();
}

/// <summary>
/// Enable/disable ReturnPolicy based on IsReturnLocationInherited value.
/// </summary>
private void enableReturnPolicy()
{
    RetailAttributesLegalEntityLookup_ds.object(fieldNum(RetailAttributesLegalEntityLookup, ReturnPolicy)).allowEdit(!ProcCategoryModifier.IsReturnPolicyInherited);
}

public void selectionChanged()
{
    super();

    LELookupStringEdit.modified();
    element.refreshCategoryProducts();

    element.refreshProductAttributes();
    element.refreshCategoryAttributes();
    element.refreshCategoryVendor();
    element.refreshCategoryLevelMap();

    // Refresh info code grid on category hierarchy selection change
    element.refreshCategoryInfoCode();

    procCategoryModifier_IsProductAttributesInherited.enabled(ecoResCategory.ParentCategory);

    if (element.getHierarchyRole() == EcoResCategoryNamedHierarchyRole::Retail)
    {
        element.refreshStaticAttributes();
    }

    element.enableManualDiscountCheckBox();

    element.setAltItemProductDimensionFields();

    ProcCategoryModifier_ds.object(fieldNum(ProcCategoryModifier, IsReturnPolicyInherited)).allowEdit(ecoResCategory.ParentCategory);
    element.enableReturnPolicy();
}

/// <summary>
/// Set the Range parameter value.
/// </summary>
public void init()
{
    QueryBuildDataSource qbds;
    QueryBuildRange qdr;

    super();

    qbds = this.queryBuildDataSource();
    qdr = qbds.addRange(fieldnum(EcoResCategoryTranslation, LanguageId));
    qdr.value(queryValue(SysQueryRangeUtil::currentLanguageId()));
}

public void executeQuery()
{
    productCategoryRange.value(queryValue(ecoResCategory.RecId));

    super();
}

/// <summary>
/// Override this method to setup the sort fields.
/// </summary>
public void init()
{
    super();

    QueryBuildDataSource productCategory_ds = this.query().dataSourceTable(tableNum(EcoResProductCategory));

    // If feature is turned on, then set the sort fields use DisplayOrder, by default, the feature is turned off
    if (FeatureStateProvider::isFeatureEnabled(RetailEnableDisplayOrderForMerchandisingFeature::instance()))
    {
        productCategory_ds.sortClear();
        productCategory_ds.addSortField(fieldNum(EcoResProductCategory, DisplayOrder), SortOrder::Ascending);
        productCategory_ds.addSortField(fieldNum(EcoResProductCategory, Product), SortOrder::Ascending);
    }

    productCategoryRange = productCategory_ds.addRange(fieldNum(EcoResProductCategory, Category));
}

/// <summary>
/// Override the write method to change all the DisplayOrder values for records shared same <Product, CategoryHierarchy>
/// This is because we need to keep the DisplayOrder value consitent when one of them is changed
/// </summary>
public void write()
{
    // If ther operaiton is not update
    if (!ecoResProductCategory.isDisplayOrderUpdated())
    {
        super();
        return;
    }

    boolean isProductAddedToHierarchyMutipleTimes = ecoResProductCategory.isProductAddedToHierarchyMutipleTimes();

    // Show the DialogBox only when feature is On, and DisplayOrder value is updated (No need when other fields are updated).
    if (FeatureStateProvider::isFeatureEnabled(RetailEnableDisplayOrderForMerchandisingFeature::instance()) && isProductAddedToHierarchyMutipleTimes)
    {
        // "Updating display order value for product '{product number} - {product name}' will update its display order value to the new value across all categories in this hierarchy. Do you want to continue?"
        str dialogText = strFmt(
            "@Retail:RetailUpdateAssociatedProductDisplayOrderValuesReminder",
            ecoResProduct.DisplayProductNumber,
            ecoResProductTranslation.Name
            );

        DialogButton response = Box::yesNo(dialogText, DialogButton::No);

        if (DialogButton::No == response)
        {
            // If No is clicked, refresh and return;
            this.reread();
            return;
        }
    }

    // If feature is not enabled, or feature is enabled, but customer select YES in the DialogBox, do the update.
    // Even when the feature is not enabled, we need to update all the associated values, or there will be incosistent.
    super();

    // Update DisplayOrder value of all the instances of the product in the category hierarchy if current DisplayOrder value is changed.
    if (isProductAddedToHierarchyMutipleTimes)
    {
        ecoResProductCategory.updateDisplayOrderForAllInstances();
    }
}

public void jumpRef()
{
    Args            args;
    MenuFunction    mf;
    EcoResProduct   product = EcoResProduct::findByDisplayProductNumber(EcoResProduct.DisplayProductNumber);

    if (!product.RecId)
    {
        return;
    }

    args = new Args();
    args.caller(element);
    args.lookupRecord(product);

    mf = new MenuFunction(menuitemDisplayStr(EcoResProductDetails), MenuItemType::Display);
    mf.run(args);
}

