public boolean validate()
{
    EcoResAttributeType attributeType;
    EcoResBoundedAttributeTypeValue attributeBound;
    EcoResValue lowerBound;
    EcoResValue upperBound;
    boolean ret;

    ret = super();

    attributeType = EcoResAttributeType::find(attr_Cat.AttributeType);
    attributeBound = EcoResBoundedAttributeTypeValue::findByAttributeType(attributeType);

    if (attributeBound)
    {
        lowerBound = EcoResValue::find(attributeBound.LowerBound);
        upperBound = EcoResValue::find(attributeBound.UpperBound);
        if (this.getValue() < lowerBound.getValue() ||
            this.getValue() > upperBound.getValue())
        {
            warning(strFmt("@SYS310456", this.getValue(), attributeBound.toString(), attributeType.Name));

            //info(strfmt("Attribute value must be between %1 and %2", lowerBound.getValue()
            ret = false;
        }
    }

    return ret;
}

