/// <summary>
/// Displays the name of the category on the path from the root category to the currenly selected category based on its level.
/// </summary>
/// <param name="_categoryLevel">
/// The <c>EcoResCategory</c> buffer storing the level of the category to display the name of.
/// </param>
/// <returns>
/// The name of the category at the specified level.
/// </returns>
display public EcoResCategoryName inheritedFrom(EcoResCategory _categoryLevel)
{
    EcoResCategoryName ret;
    if (mCategoryLevelMap.exists(_categoryLevel.Level))
    {
        ret = mCategoryLevelMap.lookup(_categoryLevel.Level);
    }
    return ret;
}

