public void executeQuery()
{
    super();

    if (!selectedDataArea.RecId)
    {	// initlize the selectedDataArea is so that it matches the LElookupStringEdit controls value...

        select firstonly selectedDataArea
        where selectedDataArea.LegalEntity == CompanyInfo::findDataArea(LELookupStringEdit.valueStr()).RecId &&
            selectedDataArea.Category == ecoResCategory.RecId;
    }

    // locate data source to the data area selected in the lookup
    RetailAttributesLegalEntityLookup_ds.positionToRecord(selectedDataArea);
}

public DataAreaId currentDataAreaId()
{
    var legalEntity = retailAttributesLegalEntityLookup.LegalEntity;
    return CompanyInfo::findRecId(legalEntity).DataArea;
}

