    this.object(fieldNum(RetailAttributesLegalEntityLookup, LabelAttribute2)).enabled(isCountryRegionRU);
    this.object(fieldNum(RetailAttributesLegalEntityLookup, LabelAttribute3)).enabled(isCountryRegionRU);
    this.object(fieldNum(RetailAttributesLegalEntityLookup, LabelAttribute4)).enabled(isCountryRegionRU);
    this.object(fieldNum(RetailAttributesLegalEntityLookup, LabelAttribute5)).enabled(isCountryRegionRU);
    //</GEERU>

    return ret;
}

