/// <summary>
/// Lookup to select return location for the selected Legal Entity.
/// </summary>
/// <param name = "_formReferenceControl"></param>
/// <returns>
/// The selected records.
/// </returns>
public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    changecompany(retailAttributesLegalEntityLookup_ds.currentDataAreaId())
    {
        Query query = new Query();
        SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tablenum(RetailReturnPolicy), _formReferenceControl);
        sysTableLookup.addLookupfield(fieldNum(RetailReturnPolicy, PolicyNumber ));
        sysTableLookup.addLookupfield(fieldNum(RetailReturnPolicy, Description));
        sysTableLookup.addLookupfield(fieldNum(RetailReturnPolicy, PolicyPrintLabels));

        query.addDataSource(tablenum(RetailReturnPolicy));
        sysTableLookup.parmQuery(query);
        return sysTableLookup.performFormLookup();
    }
}

