public void executeQuery()
{
    productSpecialCategoryRange.value(queryValue(ecoResCategory.RecId));

    super();

    numberOfSpecialCategoryMembers = SysQuery::countLoops(this.queryRun());
}

public void init()
{
    super();

    productSpecialCategoryRange = this.query().dataSourceTable(tableNum(RetailSpecialCategoryMember)).addRange(fieldNum(RetailSpecialCategoryMember, Category));
}

public void write()
{
    RecId oldProductRecId = retailGroupMemberLine.orig().Product;
    RecId oldVariantRecId = retailGroupMemberLine.orig().Variant;

    ttsbegin;

    if (retailGroupMemberLine.Product != oldProductRecId || retailGroupMemberLine.Variant != oldVariantRecId)
    {
        // remove the link from productCategory if product is changed.
        retailSpecialCategoryMember.deleteProductCategory(retailGroupMemberLine.orig());
    }

    super();

    if (retailGroupMemberLine.Product != oldProductRecId || retailGroupMemberLine.Variant != oldVariantRecId)
    {
        // Insert the record into EcoResProductCategory to link assigned product with the category.
        EcoResProductCategory newEcoResProductCategory = EcoResProductCategory::insertEcoResProductCategory(ecoResCategory,
            EcoResProduct::find(retailGroupMemberLine.Variant ? retailGroupMemberLine.Variant : retailGroupMemberLine.Product));

        // Update RetailGroupMemberLine if EcoResProductCategory does not exist.
        if (!newEcoResProductCategory.RecId)
        {
            retailGroupMemberLine.Product = 0;
            retailGroupMemberLine.Category = 0;
            retailGroupMemberLine.Variant = 0;

            retailGroupMemberLine.update();
        }

        // Create the link between special category and product on modifying the product when the flight is enabled.
        if (RetailLinkSpecialCategoryOnEditingProductFlight::instance().isEnabled())
        {
            RetailSpecialCategoryMember specialCategoryMember;

            select firstonly specialCategoryMember
                where specialCategoryMember.Category == EcoResCategory.RecId
                    && specialCategoryMember.RetailGroupMember == retailGroupMemberLine.RecId;

            if (!specialCategoryMember.RecId)
            {
                specialCategoryMember.clear();
                specialCategoryMember.RetailGroupMember = retailGroupMemberLine.RecId;
                specialCategoryMember.Category = EcoResCategory.RecId;
                specialCategoryMember.insert();
            }
        }
    }

    ttscommit;

    if (!RetailGroupMemberLine.Product && !deletingRecord)
    {
        warning("@Retail:RetailSpecifyProductWarning");
    }
}

public void init()
{
    super();

    this.cacheAddMethod(tableMethodStr(RetailGroupMemberLine, productName), true);
}

