/// <summary>
/// The datasource <c>CatyAttrGrp</c> is read-only and should never be modified.  Marking the data source
/// as not allowing Create/Update/Delete or the control as ValidateWrite set to NO will not prevent
/// the system from still tryng to save the data source.  The form usually calls validateWrite when leaveRecord api is
/// called. Leave record is called when focus on the client moves from one datasource "save boundary" to the next,
/// basically when crossing the line. Save boundary defines an area in the form hierarchy where the datasource is
/// set on the container. Thus to prevent errors for this data source when the form is in "Edit" - we will always
/// return false for validateWrite without any additional messages.
/// </summary>
/// <returns>false.</returns>
public boolean validateWrite()
{
    return false;
}

/// <summary>
/// Sort companyinfo datasource alphabetically on dataarea column.
/// </summary>
public void executeQuery()
{
    // Sort legal entity list
    companyinfo_ds.query().dataSourceName('CompanyInfo').sortClear();
    companyinfo_ds.query().dataSourceName('CompanyInfo').addSortField(fieldNum(CompanyInfo, DataArea), SortOrder::Ascending);

    super();
}

/// <summary>
/// Company range needs to be cleared and added again on user selection change of
/// </summary>
public void selectionChanged()
{
    super();

    // Refresh info code grid on legal entity selection change
    retailInfocodeCategorySpecific_ds.query().clearCompanyRange();
    retailInfocodeCategorySpecific_ds.query().addCompanyRange(companyInfo.DataArea);
    retailInfocodeCategorySpecific_ds.executeQuery();
}

