/// <summary>
/// Displays infocode prompt by calling table method in the context of selected legal entity.
/// </summary>
/// <param name = "_retailInfocodeCategorySpecific">RetailInfocodeCategorySpecific record</param>
/// <returns>Infocode prompt text</returns>
display RetailInfocodePrompt showPrompt(RetailInfocodeCategorySpecific _retailInfocodeCategorySpecific)
{
    RetailInfocodePrompt ret;

    // Change the legal entity to selected company
    changeCompany(companyInfo.DataArea)
    {
        if (_retailInfocodeCategorySpecific.InfocodeId)
        {
            ret = RetailInfocodeTable::find(_retailInfocodeCategorySpecific.InfocodeId).infocodePrompt();
        }
    }

    return ret;
}

