void clicked()
{
    Args args;
    MenuFunction menuFunction;

    args = new Args(formStr(ECoResCategory));
    args.caller(element);

    args.record(mCategoryHierarchy);

    menuFunction = new MenuFunction(this.menuItemName(), MenuItemType::Display);
    menuFunction.run(args);
}

void clicked()
{
    Args args;
    MenuFunction menuFunction;
    RetailCategoryProductUpdateParm parameters;

    args = new Args(formStr(RetailProductUpdateForm));
    args.caller(element);

    parameters = RetailCategoryProductUpdateParm::construct();
    parameters.parmEntityId(ecoResCategory.RecId);
    parameters.parmEntityName(ecoResCategory.Name);
    parameters.parmEntityType(RetailProductUpdateMode::Category);
    parameters.parmCategoryId(ecoResCategory.RecId);
    parameters.parmOldCategoryAttributes(mOriginalCategoryProperties.lookup(ecoResCategory.RecId));

    args.parmObject(parameters);

    menuFunction = new MenuFunction(this.menuItemName(), MenuItemType::Display);
    menuFunction.run(args);
}

