{
    EcoResTextValue attributeValue = element.getCategoryAttributeValue(sharedParams.VideosAttributeType);
    RetailAttributeHelper::editMediaAttributeValue(element, attributeValue, RetailRichMediaType::Videos, RetailRichMediaHelper::getUrlTemplateMapByCategory(ecoResCategory));
}

public void expanded(int _idx, FormTreeExpand _action, anytype _data)
{
    super(_idx, _action, _data);
    if (mHierarchyTree)
    {
        mHierarchyTree.onExpanded(_idx, _action);
    }
}

public boolean expanding(int _idx, FormTreeExpand _action, anytype _data)
{
    boolean ret;

    ret = super(_idx, _action, _data);

    if (mHierarchyTree)
    {
        mHierarchyTree.expanding(_idx, _action, _data);
    }

    return ret;
}

public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    super(_oldItem, _newItem, _how);

    if (mHierarchyTree)
    {
        mHierarchyTree.selectionChanged(_oldItem, _newItem);
    }
}

