void clicked()
{
    EcoResCategoryAttributeGroup ecoResCategoryAttributeGroup;
    int                     totalSelected = 0;
    int                     totalDeleted = 0;

    if (Box::yesNo("@SYS303265", DialogButton::No) == DialogButton::Yes)
    {
        ttsbegin;
        for (ecoResCategoryAttributeGroup = getFirstSelection(ecoResProductAttributeGroup_ds);
        ecoResCategoryAttributeGroup;
        ecoResCategoryAttributeGroup = ecoResProductAttributeGroup_ds.getNext())
        {
            totalSelected += 1;
            if (ecoResCategoryAttributeGroup.Category == ecoResCategory.RecId)
            {
                ecoResCategoryAttributeGroup.delete();
                totalDeleted += 1;
            }
        }
        ttscommit;

        ecoResProductAttributeGroup_ds.executeQuery();

        // some are inherited hence cannot be deleted - let use know
        if (totalSelected > totalDeleted)
        {
            info(strFmt("@RET261182", totalDeleted, (totalSelected - totalDeleted)));
        }
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    // root node doesn't need refresh
    // cannot refresh for root node - the code called below may throw error, go into infinite loop, or give wrong results
    // because the root node's InheritAttributes may be set to TRUE now which violates our assumption
    // (this is assumption is enforced by EcoResCategory.validateWrite but at this point the change to InheritAttributes
    // isn't persisted yet - hence validateWrite hasn't executed
    // )
    if ( ecoResCategory.ParentCategory)
    {
        // persist the change before UI refresh
        // if we didn't persist, then UI refresh will have no effect since we still query the db for theInheritAttribute value.
        procCategoryModifier.write();
        element.refreshProductAttributes();
    }
    //call method to update/populate Category Attribute lookup table
    EcoResCategoryAttributeLookup::synchronizeWithCategoryAttribute(ecoResCategory);

    return ret;
}

void clicked()
{
    if (DialogButton::Ok == Box::okCancel("@RET4170025",DialogButton::Cancel))
    {
        ttsbegin;
        if (ecoResValue_Category.RecId)
        {
            ecoResValue_Category.selectForUpdate(true);
            ecoResValue_Category.delete();
        }

        if (ecoResAttributeValue_Category.RecId)
        {
            ecoResAttributeValue_Category.selectForUpdate(true);
            ecoResAttributeValue_Category.delete();
        }
        ttscommit;

        catyAttrGrp_ds.executeQuery();
    }
}

