public boolean validate()
{
    boolean isValid;
    EcoResFloatValue ecoResFloatValue;
    EcoResAttributeTypeUnitOfMeasure ecoResAttributeTypeUnitOfMeasure = EcoResAttributeTypeUnitOfMeasure::findByAttributeType(attrType_Cat);

    isValid = super();

    ecoResFloatValue.FloatValue = this.realValue();
    if (ecoResAttributeTypeUnitOfMeasure.RecId)
    {
        ecoResFloatValue.FloatUnitOfMeasure = UnitOfMeasure::find(ecoResAttributeTypeUnitOfMeasure.DefaultUnitOfMeasure).RecId;
    }
    if (bndVal_Cat && !bndVal_Cat.isValid(ecoResFloatValue))
    {
        isValid = false;
        warning(strFmt("@SYS310456", this.realValue(), bndVal_Cat.toString(), attrType_Cat.Name));
    }

    return isValid;
}

public boolean validate()
{
    boolean isValid;
    EcoResIntValue ecoResIntValue;
    EcoResAttributeTypeUnitOfMeasure ecoResAttributeTypeUnitOfMeasure = EcoResAttributeTypeUnitOfMeasure::findByAttributeType(attrType_Cat);

    isValid = super();

    ecoResIntValue.IntValue = this.value();
    if (ecoResAttributeTypeUnitOfMeasure.RecId)
    {
        ecoResIntValue.IntUnitOfMeasure = UnitOfMeasure::find(ecoResAttributeTypeUnitOfMeasure.DefaultUnitOfMeasure).RecId;
    }
    if (bndVal_Cat && !bndVal_Cat.isValid(ecoResIntValue))
    {
        isValid = false;
        warning(strFmt("@SYS310456", this.value(), bndVal_Cat.toString(), attrType_Cat.Name));
    }

    return isValid;
}

public boolean validate()
{
    boolean isValid;
    EcoResCurrencyValue ecoResCurrencyValue;

    isValid = super();

    ecoResCurrencyValue.CurrencyValue = categoryCurrencyValueRealEdit.realValue();
    ecoResCurrencyValue.CurrencyCode = categoryCurrencyCode.text();
    ecoResCurrencyValue.insert();
    if (bndVal_Cat && !bndVal_Cat.isValid(ecoResCurrencyValue))
    {
        isValid = false;
        warning(strFmt("@SYS310456", ecoResCurrencyValue.value(), bndVal_Cat.toString(), attrType_Cat.Name));
    }
    ttsbegin;
    ecoResCurrencyValue.delete();
    ttscommit;

    return isValid;
}

public boolean validate()
{
    boolean isValid;
    EcoResDateTimeValue ecoResDateTimeValue;

    isValid = super();

    ecoResDateTimeValue.DateTimeValue = this.dateTimeValue();
    if (bndVal_Cat && !bndVal_Cat.isValid(ecoResDateTimeValue))
    {
        isValid = false;
        warning(strFmt("@SYS310456", this.dateTimeValue(), bndVal_Cat.toString(), attrType_Cat.Name));
    }

    return isValid;
}
