[Form]
public class RetailCategoryAndProductAssortment extends FormRun implements SysIFilterEventHandler
{
    const str DetailsOption_Channels = "@Retail:AssortmentDetailOptionChannels";

}

public void init()
{
    super();
    this.onFilterChanged();

    ToggleProductDetailsSection.OnClicked += eventhandler(this.toggleDetailSectionVisibility);

    DetailsSectionSelector.add(DetailsOption_Channels);

    // Set details section to show product variants.
    this.selectDetailType(DetailsOption_Channels);
}

/// <summary>
/// Event handler to react on a filter change.
/// </summary>
public void onFilterChanged()
{
    this.applyOrgHierarchyFilter();
    this.applyCategoryFilter();
    RetailAssortmentTable_DS.executeQuery();
}

/// <summary>
/// Retrieves the workspace level org hierachy filter from <c>RetailCategoryAndProductWorkspace</c>
/// and applies it to the current form <c>RetailCategoryAndProductAssortment</c>.
/// </summary>
private void applyOrgHierarchyFilter()
{
    // Retrieve the org hierarchy filter from the parent form
    SysIFilter  filter = this.parmFilterProvider().parmFilter();
    RecId omHierarchyRelationshipRecId = SysFilterValueHelper::getFirstValueRecordFromFilterForType(filter, SysFilterValueTypeEnum::Integer, extendedTypeNum(RecId));

    ChannelFilter_OMHierarchyRelationship_DS.queryBuildDataSource().clearRanges();

    // If org hierarchy filter is blank disable ChannelFilter_RetailAssortmentChannelLine datasource so as to show all assortments
    // including those which are not associated to any org or channel.
    if (omHierarchyRelationshipRecId == 0)
    {
        ChannelFilter_RetailAssortmentChannelLine_DS.queryBuildDataSource().enabled(false);
    }
    else
    {
        ChannelFilter_RetailAssortmentChannelLine_DS.queryBuildDataSource().enabled(true);

        // Filter only the assortments which are associated to the channels specified by the current org hierarchy filter.
        QueryBuildRange omHierarchyRelationShipRange = ChannelFilter_OMHierarchyRelationship_DS.queryBuildDataSource().addRange(fieldNum(OMHierarchyRelationship, RecId));
        omHierarchyRelationShipRange.value(queryValue(omHierarchyRelationshipRecId));
    }
}

/// <summary>
/// Retrieves the workspace level category filter from <c>RetailCategoryAndProductWorkspace</c>
/// and applies it to the current form <c>RetailCategoryAndProductAssortment</c>.
/// </summary>
private void applyCategoryFilter()
{
    // Retrieve the workspace level category filter from the parent form RetailCategoryAndProductWorkspace.
    SysIFilter  filter = this.parmFilterProvider().parmFilter();

    EcoResCategoryHierarchyId categoryHierarchyId = SysFilterValueHelper::getFirstValueRecordFromFilterForType(filter, SysFilterValueTypeEnum::Integer, extendedTypeNum(EcoResCategoryHierarchyId));

    CategoryFilter_EcoResCategory_DS.queryBuildDataSource().clearRanges();

    // If categoy filter is blank disable CategoryFilter_RetailAssortmentProductLine datasource to show all assortments including those which do not have any product lines.
    if (categoryHierarchyId == 0)
    {
        CategoryFilter_RetailAssortmentProductLine_DS.queryBuildDataSource().enabled(false);
    }
    else
    {
        // If category filter is not blank then enable the join on CategoryFilter_RetailAssortmentProductLine datasource and
        // apply the category filter on EcoResCategory datasource ranges.

        CategoryFilter_RetailAssortmentProductLine_DS.queryBuildDataSource().enabled(true);

        // apply category filter.
        QueryBuildDataSource EcoResCategoryQBDS = CategoryFilter_EcoResCategory_DS.queryBuildDataSource();

        EcoResCategoryNestedSetRight categoryNestedSetRight = SysFilterValueHelper::getFirstValueRecordFromFilterForType(filter, SysFilterValueTypeEnum::Integer, extendedTypeNum(EcoResCategoryNestedSetRight));
        EcoResCategoryNestedSetLeft categoryNestedSetLeft = SysFilterValueHelper::getFirstValueRecordFromFilterForType(filter, SysFilterValueTypeEnum::Integer, extendedTypeNum(EcoResCategoryNestedSetLeft));

        QueryBuildRange categoryHierarchyRange = EcoResCategoryQBDS.addRange(fieldNum(EcoResCategory, CategoryHierarchy));
        categoryHierarchyRange.value(queryValue(categoryHierarchyId));

        QueryBuildRange nestedSetLeftRange = EcoResCategoryQBDS.addRange(fieldNum(EcoResCategory, NestedSetLeft));
        nestedSetLeftRange.value(Global::queryRange(categoryNestedSetLeft, categoryNestedSetRight));

        QueryBuildRange nestedSetRightRange = EcoResCategoryQBDS.addRange(fieldNum(EcoResCategory, NestedSetRight));
        nestedSetRightRange.value(Global::queryRange(categoryNestedSetLeft, categoryNestedSetRight));

        QueryBuildRange isActiveRange = EcoResCategoryQBDS.addRange(fieldNum(EcoResCategory, IsActive));
        isActiveRange.value(queryValue(NoYes::Yes));
    }
}

private void toggleDetailSectionVisibility(FormControl _sender, FormControlEventArgs _e)
{
    FormButtonControl switchButton = _sender as FormButtonControl;

    AssortmentDetailGroup.visible(!AssortmentDetailGroup.visible());

    if (AssortmentDetailGroup.visible())
    {
        switchButton.normalImage(ImageReferenceSymbol::CaretLeft);
        switchButton.text("@Retail:HideDetails");
    }
    else
    {
        switchButton.normalImage(ImageReferenceSymbol::CaretRight);
        switchButton.text("@Retail:ShowDetails");
    }
}

/// <summary>
/// Applies the assortment status filter and executes the query.
/// </summary>
public void executeQuery()
{
    this.applystatusfilter();
    super();
}

private void applystatusfilter()
{
    Query qry = this.query();
    qry.dataSourceTable(tableNum(RetailAssortmentTable)).clearRange(fieldNum(RetailAssortmentTable, Status));
    QueryBuildRange qbr = qry.dataSourceTable(tableNum(RetailAssortmentTable)).addRange(fieldNum(RetailAssortmentTable, Status));
    qbr.value(AssortmentStatusFilter.valueStr());
}

public void selectionChanged()
{
    super();

    // add currently selected assortment's RecId as range to RetailAssortmentChannelLine datasource.

    RetailAssortmentChannelLine_DS.queryBuildDataSource().clearRanges();

    QueryBuildRange qbr = RetailAssortmentChannelLine_DS.queryBuildDataSource().addRange(fieldNum(RetailAssortmentChannelLine, AssortmentRecId));
    qbr.value(SysQuery::value(RetailAssortmentTable.RecId));

    // execute query after assortment recid is added as range.
    // this will calculate the exploded list of channels included in the current assortment.
    // RetailChannelTable exist join OMExplodedOrganizationSecurityGraph  exist join RetailAssortmentChannelLine where RetailAssortmentChannelLine.AssortmentRecId = [selected assortmentRecId]
    RetailChannelTable_DS.executeQuery();
}

public void init()
{
    super();

    // add table relationship between  OMExplodedOrganizationSecurityGraph and RetailAssortmentChannelLine to be used the the exit join.
    QueryBuildDataSource qbds = ChannelFilter_OMExplodedOrganizationSecurityGraph_DS.queryBuildDataSource();
    qbds.relations(false);
    qbds.addLink(fieldNum(RetailAssortmentChannelLine, OMInternalOrganizationId), fieldNum(OMExplodedOrganizationSecurityGraph, Organization));
}

public void init()
{
    super();

    // add table relationship between  OMHierarchyRelationship and OMExplodedOrganizationSecurityGraph to be used the the exit join.
    QueryBuildDataSource qbds = ChannelFilter_OMHierarchyRelationship_DS.queryBuildDataSource();
    qbds.relations(false);
    qbds.addLink(fieldNum(OMExplodedOrganizationSecurityGraph, AncestorOrganization), fieldNum(OMHierarchyRelationship, ChildOrganization));
}

public void init()
{
    super();
    // add table relationship between  RetailChannelTable and OMExplodedOrganizationSecurityGraph to be used the the exit join.
    QueryBuildDataSource qbds = OMExplodedOrganizationSecurityGraph_DS.query().dataSourceTable(tableNum(OMExplodedOrganizationSecurityGraph));
    qbds.relations(false);
    qbds.addLink(fieldNum(RetailChannelTable, OMOperatingUnitID), fieldNum(OMExplodedOrganizationSecurityGraph, Organization));
}

public void init()
{
    super();
    // add table relationship between  RetailChannelTable and OMExplodedOrganizationSecurityGraph to be used the the exit join.
    QueryBuildDataSource qbds = RetailAssortmentChannelLine_DS.query().dataSourceTable(tableNum(RetailAssortmentChannelLine));
    qbds.relations(false);
    qbds.addLink(fieldNum(OMExplodedOrganizationSecurityGraph,AncestorOrganization), fieldNum(RetailAssortmentChannelLine,OMInternalOrganizationId));
}

/// <summary>
/// Applies the assortment status filter specified by the user.
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    RetailAssortmentTable_DS.executeQuery();

    return ret;
}
