[Form]
public class RetailCategoryPriceRule extends FormRun implements SysIFilterConsumerForm, SysIFilterEventHandler
{
    EcoResCategoryTreeDatasource    mHierarchyTree;

    QueryBuildRange                 nestedLeftRange,
                                    nestedRightRange,
                                    categoryHierarchyRange;
    boolean                         isCategoryTreeView;
    container                       hierarchyRoles;
    InventDimCtrl_Frm               inventDimFormSetup;
}

public void init()
{
    RetailCategoryPriceRuleUpdate update;

    // <GEERU>
    isCategoryTreeView = element.args().dataset() == tablenum(RetailCategoryPriceRuleGroup);
    // </GEERU>

    hierarchyRoles = RetailCategoryPriceRule::allowedCategoryHierarchyRoles();

    update = RetailCategoryPriceRuleUpdate::construct();
    update.run();

    super();

    this.updateDesign(InventDimFormDesignUpdate::Init);
    this.initRecord();
    // <GEERU>
    if (isCategoryTreeView)
    {
        element.design().caption("@SYP4880850");

        CategoryHierarchyReferenceGroup.visible(true);
        HierarchyTreeCtrl.visible(true);
        VerticalSplitter.visible(true);

        this.initializeCategoryHierarchy();
        this.initializeCategoryTree();
    }
    // </GEERU>
}

void initRecord()
{
    boolean canEdit = RetailCategoryPriceRule.AccountCode != TableGroupAll::All;

    RetailCategoryPriceRule_ds.object(fieldNum(RetailCategoryPriceRule, AccountRelation)).skip(!canEdit);
    RetailCategoryPriceRule_ds.object(fieldNum(RetailCategoryPriceRule, AccountRelation)).allowEdit(canEdit);
}

void resetGeneratePricesButton()
{
    GeneratePricesMenuItemButton.enabled(RetailCategoryPriceRule.isReady4GeneratingPrices());
}

    /// <summary>
    /// Returns whether price basis type is related to purchase type or not.
    /// </summary>
    /// <param name = "_priceBasis">Price basis.</param>
    /// <returns>
    /// True if price basis type is related to purchase type; otherwise, false.
	/// </returns>
	boolean isPriceBasisPurchasePrice(RetailPriceBasisType _priceBasis)
    {
        return _priceBasis == RetailPriceBasisType::PurchasePrice
            || _priceBasis == RetailPriceBasisType::LastPurchasePrice;
    }

/// <summary>
/// Returns whether price basis type is related to invent cost or not.
/// </summary>
/// <param name = "_priceBasis">Price basis.</param>
/// <returns>
/// True if price basis type is related to invent cost; otherwise, false.
/// </returns>
boolean isPriceBasisInventCost(RetailPriceBasisType _priceBasis)
{
    return _priceBasis == RetailPriceBasisType::InventCost;
}

    /// <summary>
    /// Returns whether price basis type is related to other price group or not.
    /// </summary>
    /// <param name = "_priceBasis">Price basis.</param>
    /// <returns>
    /// True if price basis type is related to other price group; otherwise, false.
    /// </returns>
    boolean isPriceBasisOtherPriceGroup(RetailPriceBasisType _priceBasis)
    {
        return _priceBasis == RetailPriceBasisType::OtherPriceGroup;
	}

void allowEditControls()
{
    boolean allowEditPurchasePrice		= this.isPriceBasisPurchasePrice(retailCategoryPriceRule.PriceBasis);
    boolean allowEditInventCost			= this.isPriceBasisInventCost(retailCategoryPriceRule.PriceBasis);
    boolean allowEditOtherPriceGroup	= this.isPriceBasisOtherPriceGroup(retailCategoryPriceRule.PriceBasis);

    RetailCategoryPriceRule_ds.object(fieldNum(RetailCategoryPriceRule, PriceBasisCurrencyCode)).allowEdit(allowEditPurchasePrice || allowEditOtherPriceGroup);
    RetailCategoryPriceRule_ds.object(fieldNum(RetailCategoryPriceRule, PriceBasisVendAccount)).allowEdit(allowEditPurchasePrice);
    RetailCategoryPriceRule_ds.object(fieldNum(RetailCategoryPriceRule, ExchangeRateTypeRecId)).allowEdit(allowEditPurchasePrice || allowEditInventCost || allowEditOtherPriceGroup);
    RetailCategoryPriceRule_ds.object(fieldNum(RetailCategoryPriceRule, PriceBasisOtherPriceGroup)).allowEdit(allowEditOtherPriceGroup);

    InventDim_ds.allowEdit(allowEditPurchasePrice || allowEditInventCost);
}

private EcoResCategoryHierarchy getCategoryHierarchy()
{
    return EcoResCategoryHierarchy::find(categoryHierarchyReferenceGroup.value());
}

private void initializeCategoryHierarchy()
{
    RetailCategoryPriceRuleGroup callerGroupRecord;
    EcoResCategoryHierarchyId hierarchy;

    if (element.args().dataset() == tableNum(RetailCategoryPriceRuleGroup))
    {
        callerGroupRecord = element.args().record() as RetailCategoryPriceRuleGroup;
        hierarchy = callerGroupRecord.EcoResCategoryHierarchy;
    }
    else
    {
        hierarchy = EcoResCategoryHierarchy::findDefaultFormHierarchyRecId();
    }

    categoryHierarchyReferenceGroup.value(hierarchy);
}

public void initializeCategoryTree()
{
    EcoResCategoryLookupParameters lookupParameters = new EcoResCategoryLookupParameters(false, false, false);
    EcoResCategoryHierarchy retailCategoryHierarchy = this.getCategoryHierarchy();
    EcoResCategory root = EcoResCategory::getRoot(retailCategoryHierarchy.RecId);

    mHierarchyTree = new EcoResCategoryTreeDatasource( ecoResCategoryTree_ds,
                                                       hierarchyTreeCtrl,
                                                       fieldNum(EcoResCategory, RecId),
                                                       fieldNum(EcoResCategory, ParentCategory),
                                                       fieldNum(EcoResCategory, Name),
                                                       false /* No ID in description */,
                                                       true,
                                                       fieldNum(EcoResCategory, Code),
                                                       retailCategoryHierarchy,
                                                       null,
                                                       lookupParameters,
                                                       fieldNum(EcoResCategory, NestedSetLeft) /* for tree sort order */
                                                       );

    mHierarchyTree.initRoot(root.Name, root.RecId, mHierarchyTree.image(root) );
}

InventDimCtrl_Frm inventDimSetupObject()
{
    return inventDimFormSetup;
}

public void syncRuleDSWithTree()
{
    nestedLeftRange.value(SysQuery::range(EcoResCategoryTree.NestedSetLeft, EcoResCategoryTree.NestedSetRight));
    nestedRightRange.value(SysQuery::range(0, EcoResCategoryTree.NestedSetRight));
    categoryHierarchyRange.value(queryValue(EcoResCategoryTree.CategoryHierarchy));

    RetailCategoryPriceRule_DS.executeQuery();
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (! inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl::newFromForm(element);
            }
            // Use same object for controls in group visibility as for controls in grid visibility
            inventDimFormSetup.parmDimParmVisible(inventDimFormSetup.parmDimParmVisibleGrid());
            inventDimFormSetup.parmDimParmEnabled(InventDimParm::allInventDim());
            inventDimFormSetup.formSetControls(true);

            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

