{
    RetailCategoryPriceRule_DS.executeQuery();
}

public void selectionChanged()
{
    super();

    element.resetGeneratePricesButton();
    element.allowEditControls();
}

public void init()
{
    QueryBuildDataSource qbds;
    #define.EcoResCategoryRuleDSName('EcoResCategoryRule')

    super();

    element.resetGeneratePricesButton();
     // <GEERU>
    if (isCategoryTreeView)
    {
        qbds = this.queryBuildDataSource().addDataSource(tableNum(EcoResCategory), #EcoResCategoryRuleDSName);
        qbds.relations(true);
        qbds.joinMode(JoinMode::ExistsJoin);

        nestedLeftRange         = findOrCreateRange_W(qbds, fieldNum(EcoResCategory, NestedSetLeft));
        nestedRightRange        = findOrCreateRange_W(qbds, fieldNum(EcoResCategory, NestedSetRight));
        categoryHierarchyRange  = findOrCreateRange_W(qbds, fieldNum(EcoResCategory, CategoryHierarchy));

        nestedLeftRange.status(RangeStatus::Locked);
        nestedRightRange.status(RangeStatus::Locked);
        categoryHierarchyRange.status(RangeStatus::Locked);
    }
     // </GEERU>
}

public void initValue()
{
    super();

    if (isCategoryTreeView)
    {
        retailCategoryPriceRule.Category = ecoResCategoryTree.RecId;
    }
}

public boolean validateWrite()
{
    boolean ret;

    retailCategoryPriceRule.PriceBasisInventDimId = InventDim::findOrCreate(inventDim).inventDimId;

    ret = super();

    return ret;
}

public void write()
{
    ttsBegin;

    super();

    if (retailCategoryPriceRule.PriceBasisInventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(retailCategoryPriceRule.PriceBasisInventDimId));
        inventDim_ds.setCurrent();
    }

    ttsCommit;
}

