public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    EcoResCategoryHierarchyRole retailHierarchyRole;
    EcoResCategoryHierarchy retailHierarchy;

    if (!isCategoryTreeView)
    {
    select firstonly CategoryHierarchy from retailHierarchyRole
        where retailHierarchyRole.NamedCategoryHierarchyRole == EcoResCategoryNamedHierarchyRole::Retail;

    retailHierarchy = EcoResCategoryHierarchy::find(retailHierarchyRole.CategoryHierarchy);
    }
    else
    {
        retailHierarchy = EcoResCategoryHierarchy::find(EcoResCategoryTree.CategoryHierarchy);
    }

    return EcoResCategory::lookupCategoryHierarchy(
                _formReferenceControl,
                retailHierarchy,
                false,
                true,
                true,
                null,
                0,
                0,
                ProcCategoryOrganizationOption::BuyingLegalEntity,
                0,
                hierarchyRoles,
                0
                );
}

public void modified()
{
    super();

    if (InventDim::findDim(inventDim).inventDimId != RetailCategoryPriceRule.PriceBasisInventDimId)
    {
        inventDim.data(InventDim::find(RetailCategoryPriceRule.PriceBasisInventDimId));
        inventDim_ds.setCurrent();
    }

    element.allowEditControls();
}

