[Form]
public class RetailCommerceValidatorResultsByEntity extends FormRun
{
    private Query formQuery;
    private Query queryForPassedCounters;
    private QueryBuildRange qbRangeValidationId;
    private QueryBuildRange qbRangeProductId;
    private QueryBuildRange qbRangeVariantRecId;
    private QueryBuildRange qbRangeCatalogRecId;
    private QueryBuildRange qbRangeCategoryRecId;
    private QueryBuildRange qbrGenericRecId;
    private QueryBuildRange qbrHierarchyRecId;
    private QueryBuildRange qbrAttributeRecId;
    private QueryBuildRange qbrAttributeGrpRecId;
    private RetailCommerceValidatorId validationId;
    private RefRecId productRecId;
    private RefRecId catalogRecId;
    private RefRecId categoryRecId;
    private RefRecId genericRecId;
    private RefRecId hierarchyRecId;
    private RefRecId AttributeRecId;
    private RefRecId attributeGrpRecId;
    private RefRecId currentBatchJob;
    private RefRecId passedCatalogRecId;
    private RefRecId passedChannelRecId;
    private RefRecId passedProductRecId;
    private RefRecId passedCategoryRecId;
    private RefRecId passedAttributeRecId;
    private RefRecId passedAttributeGrpRecId;
    private RefRecId passedCategoryHierarchyRecId;

    private RetailCommerceValidator commerceValidator;

}

/// <summary>
///  Initializes the form
/// </summary>
public void init()
{
    element.getCallerValidatorRecord();

    super();

    ToggleEntityResultsSection.OnClicked += eventhandler(this.toggleEntityResultsSectionVisibility);
    ToggleEntityPassedResultsSection.OnClicked += eventhandler(this.togglePassedEntityGridVisibility);

    // Show product when form loads.
    element.setGridFieldsVisibility(true);

    if (!PassedEntities.visible())
    {
        CatalogPassed_DS.queryBuildDataSource().enabled(false);
        EcoResProductPassed_DS.queryBuildDataSource().enabled(false);
        EcoResCategoryPassed_DS.queryBuildDataSource().enabled(false);
        EcoResAttributePassed_DS.queryBuildDataSource().enabled(false);
        RetailChannelTablePassed_DS.queryBuildDataSource().enabled(false);
        EcoResAttributeGroupPassed_DS.queryBuildDataSource().enabled(false);
        EcoResCategoryHierarchyPassed_DS.queryBuildDataSource().enabled(false);
        RetailCommerceValidatorLogContextPassedResults_DS.queryBuildDataSource().enabled(false);
    }
}

public void run()
{
    super();

    // Override the preselected entity type if it's set in RetailCommerceValidatorResultsWorkSpace.
    FormRun formRun = element.args().caller();
    if (formRun &&
        formRun.name() == formStr(RetailCommerceValidatorResultsWorkSpace) &&
        formHasMethod(formRun, identifierStr(parmPreselectedEntityType)))
    {
        EntityType.selection(formRun.parmPreselectedEntityType());
        EntityType.selectionChange();
        this.toggleEntityResultsSectionVisibility(ToggleEntityResultsSection, null);
    }
}

/// <summary>
/// Gets the <c>RetailCommerceValidator</c> tables buffer of caller.
/// </summary>
private void getCallerValidatorRecord()
{
    FormRun formRun = element.args().caller();
    FormDataSource commerceValidatorDataSource;

    if(formRun)
    {
        for (int i = 0; i <= formRun.dataSourceCount(); i++)
        {
            // First data source is RetailCommerceValidator
            commerceValidatorDataSource = formRun.dataSource(i);

            if (commerceValidatorDataSource.table() == tableNum(RetailCommerceValidator))
            {
                commerceValidator = commerceValidatorDataSource.cursor();
                break;
            }
        }
    }

    validationId = commerceValidator.ValidationId;
    currentBatchJob = commerceValidator.CurrentBatchJobId;
}

/// <summary>
/// Reset the applied range values.
/// </summary>
private void resetRangeValues()
{
    productRecId = 0;
    catalogRecId = 0;
    categoryRecId = 0;
    genericRecId =  0;
    hierarchyRecId = 0;
    AttributeRecId = 0;
    attributeGrpRecId = 0;
}

/// <summary>
/// Adds Validation id and CurrentBatchJobId range.
/// </summary>
private void addValidationAndBatchRange()
{
    QueryBuildDataSource qbdsLogContext = formQuery.dataSourceName('RetailCommerceValidatorLogContext');

    qbdsLogContext.addRange(fieldNum(RetailCommerceValidatorLogContext,ValidationId)).value(SysQuery::value(validationId));
    qbdsLogContext.addRange(fieldNum(RetailCommerceValidatorLogContext,BatchJobId)).value(SysQuery::value(currentBatchJob));
}

private void addRangeOnLogType()
{
    QueryBuildDataSource qbdsLogWithIssues = formQuery.dataSourceName('RetailCommerceValidatorLogWithIssues');

    qbdsLogWithIssues.clearRanges();
    qbdsLogWithIssues.addRange(fieldNum(RetailCommerceValidatorLog, LogType)).value(SysQuery::valueNot(RetailCommerceValidatorLogType::Info));
}

private void prepareEcoResProductQuery()
{
    QueryBuildDataSource qbdsMasterProducts = formQuery.dataSourceName('EcoResProduct');
    QueryBuildDataSource qbdsProductTrans = formQuery.dataSourceName('EcoResProductTranslation');

    qbdsMasterProducts.addGroupByField(fieldNum(EcoResProduct, DisplayProductNumber));
    qbdsMasterProducts.addGroupByField(fieldNum(EcoResProduct, RecId));

    qbdsProductTrans.addGroupByField(fieldNum(EcoResProductTranslation, Name));
    qbdsProductTrans.addRange(fieldNum(EcoResProductTranslation, LanguageId)).value(SystemParameters::getSystemLanguageId());
}

private void prepareStandaloneAndMasterProductsQuery()
{
    QueryBuildDataSource qbdsLogContext = formQuery.dataSourceName('RetailCommerceValidatorLogContext');

    formQuery.clearGroupBy();

    qbdsLogContext.clearRanges();
    qbdsLogContext.addGroupByField(fieldNum(RetailCommerceValidatorLogContext,ValidationId));
    qbdsLogContext.addGroupByField(fieldNum(RetailCommerceValidatorLogContext,ProductRefRecId));

    qbdsLogContext.addRange(fieldNum(RetailCommerceValidatorLogContext, ProductRefRecId)).value(SysQuery::valueNot(0));
    qbdsLogContext.addRange(fieldNum(RetailCommerceValidatorLogContext,VariantRefRecId)).value(SysQuery::value(0));
    qbdsLogContext.addRange(fieldNum(RetailCommerceValidatorLogContext,KitRefRecId)).value(SysQuery::value(0));

    element.addValidationAndBatchRange();
    element.addRangeOnLogType();
}

