private void prepareCatalogsQuery()
{
    QueryBuildDataSource qbdsLogContext = formQuery.dataSourceName('RetailCommerceValidatorLogContext');
    QueryBuildDataSource qbdsCatalog = formQuery.dataSourceTable(tableNum(Catalog));
    QueryBuildDataSource qbdsCatalogTrans = formQuery.dataSourceTable(tableNum(CatalogTranslation));

    formQuery.clearGroupBy();

    qbdsLogContext.clearRanges();
    qbdsLogContext.addGroupByField(fieldNum(RetailCommerceValidatorLogContext,ValidationId));
    qbdsLogContext.addGroupByField(fieldNum(RetailCommerceValidatorLogContext,CatalogRefRecId));

    qbdsLogContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CatalogRefRecId)).value(SysQuery::valueNot(0));

    qbdsCatalog.addGroupByField(fieldNum(Catalog, CatalogId));
    qbdsCatalog.addGroupByField(fieldNum(Catalog, RecId));

    qbdsCatalogTrans.addGroupByField(fieldNum(CatalogTranslation, FriendlyName));
    qbdsCatalogTrans.addRange(fieldNum(CatalogTranslation, Language)).value(SystemParameters::getSystemLanguageId());

    element.addValidationAndBatchRange();
    element.addRangeOnLogType();
}

private void prepareCategoryQuery()
{
    QueryBuildDataSource qbdsLogContext = formQuery.dataSourceName('RetailCommerceValidatorLogContext');
    QueryBuildDataSource qbdsCategory= formQuery.dataSourceName('EcoResCategory');

    formQuery.clearGroupBy();

    qbdsLogContext.clearRanges();

    qbdsLogContext.addGroupByField(fieldNum(RetailCommerceValidatorLogContext,ValidationId));
    qbdsLogContext.addGroupByField(fieldNum(RetailCommerceValidatorLogContext,CategoryRefRecId));

    qbdsLogContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CategoryRefRecId)).value(SysQuery::valueNot(0));

    qbdsCategory.addGroupByField(fieldNum(EcoResCategory, Name));
    qbdsCategory.addGroupByField(fieldNum(EcoResCategory, CategoryHierarchy));
    qbdsCategory.addGroupByField(fieldNum(EcoResCategory, RecId));

    // Prepare Category hierarchy query to display Category hierarchies of a category in Category entity results.
    QueryBuildDataSource qbdsCategoryHierarchy= formQuery.dataSourceName('EcoResCategoryCategoryHierarchy');

    qbdsCategoryHierarchy.clearLinks();
    qbdsCategoryHierarchy.relations(false);
    qbdsCategoryHierarchy.addLink(fieldNum(EcoResCategory,CategoryHierarchy), fieldNum(EcoResCategoryHierarchy, RecId), 'EcoResCategory');

    qbdsCategoryHierarchy.addGroupByField(fieldNum(EcoResCategoryHierarchy, Name));
    qbdsCategoryHierarchy.addGroupByField(fieldNum(EcoResCategoryHierarchy, RecId));

    element.addValidationAndBatchRange();
    element.addRangeOnLogType();
}

