private void prepareLogCounterByContextQuery()
{
    RetailCommerceValidatorTestStage entityTypeLoc = EntityType.selection();
    QueryBuildDataSource qbdsLogCounters = formQuery.dataSourceTable(tableNum(RetailCommerceValidatorLogCountersByContext));

    qbdsLogCounters.clearRanges();
    qbdsLogCounters.clearLinks();

    // Consider the CommerceChannel as entity type when Generic is selected.
    entityTypeLoc = (entityTypeLoc == RetailCommerceValidatorTestStage::Generic) ? RetailCommerceValidatorTestStage::CommerceChannels : entityTypeLoc;

    switch(entityTypeLoc)
    {
        case RetailCommerceValidatorTestStage::Products:
        case RetailCommerceValidatorTestStage::ProductVariants:
        case RetailCommerceValidatorTestStage::ProductKits:
            qbdsLogCounters.addLink(fieldNum(RetailCommerceValidatorLogContext,ProductRefRecId),
                fieldNum(RetailCommerceValidatorLogCountersByContext, EntityRefRecId),
                'RetailCommerceValidatorLogContext');
            qbdsLogCounters.joinMode(JoinMode::OuterJoin);
            break;
        case RetailCommerceValidatorTestStage::CommerceCatalogs:
            qbdsLogCounters.addLink(fieldNum(RetailCommerceValidatorLogContext,CatalogRefRecId),
                fieldNum(RetailCommerceValidatorLogCountersByContext, EntityRefRecId),
                'RetailCommerceValidatorLogContext');
            qbdsLogCounters.joinMode(JoinMode::OuterJoin);
            break;
        case RetailCommerceValidatorTestStage::CommerceCategories:
            qbdsLogCounters.addLink(fieldNum(RetailCommerceValidatorLogContext,CategoryRefRecId),
                fieldNum(RetailCommerceValidatorLogCountersByContext, EntityRefRecId),
                'RetailCommerceValidatorLogContext');
            qbdsLogCounters.joinMode(JoinMode::OuterJoin);
            break;
        case RetailCommerceValidatorTestStage::CategoryHierarchies:
            qbdsLogCounters.addLink(fieldNum(RetailCommerceValidatorLogContext,CategoryHierarchyRefRecId),
                fieldNum(RetailCommerceValidatorLogCountersByContext, EntityRefRecId),
                'RetailCommerceValidatorLogContext');
            qbdsLogCounters.joinMode(JoinMode::OuterJoin);
            break;
        case RetailCommerceValidatorTestStage::CommerceAttributes:
            qbdsLogCounters.addLink(fieldNum(RetailCommerceValidatorLogContext,AttributeRefRecId),
                fieldNum(RetailCommerceValidatorLogCountersByContext, EntityRefRecId),
                'RetailCommerceValidatorLogContext');
            qbdsLogCounters.joinMode(JoinMode::OuterJoin);
            break;
        case RetailCommerceValidatorTestStage::CommerceAttributeGroups:
            qbdsLogCounters.addLink(fieldNum(RetailCommerceValidatorLogContext,AttributeGroupRefRecId),
                fieldNum(RetailCommerceValidatorLogCountersByContext, EntityRefRecId),
                'RetailCommerceValidatorLogContext');
            qbdsLogCounters.joinMode(JoinMode::OuterJoin);
            break;
        case RetailCommerceValidatorTestStage::CommerceChannels:
            qbdsLogCounters.addLink(fieldNum(RetailCommerceValidatorLogContext,OMOperatingUnitId),
                fieldNum(RetailCommerceValidatorLogCountersByContext, EntityRefRecId),
                'RetailCommerceValidatorLogContext');
            qbdsLogCounters.joinMode(JoinMode::OuterJoin);
            break;
    }

    qbdsLogCounters.addRange(fieldNum(RetailCommerceValidatorLogCountersByContext, ValidationId)).value(SysQuery::value(commerceValidator.ValidationId));
    qbdsLogCounters.addRange(fieldNum(RetailCommerceValidatorLogCountersByContext, BatchJobId)).value(SysQuery::value(commerceValidator.CurrentBatchJobId));
    qbdsLogCounters.addRange(fieldNum(RetailCommerceValidatorLogCountersByContext, IsTemp)).value(SysQuery::value(NoYes::No));

    // As the data for main datasource(RetailCommerceValidatorLogContext) on the form is fetched by using group by. So to have values in ErrorCount/WarningCount/MessageCount fields
    // We need to include those fields in group by list or in selection field list.
    qbdsLogCounters.addSelectionField(fieldNum(RetailCommerceValidatorLogCountersByContext, ErrorCount), SelectionField::Max);
    qbdsLogCounters.addSelectionField(fieldNum(RetailCommerceValidatorLogCountersByContext, WarningCount), SelectionField::Max);
    qbdsLogCounters.addSelectionField(fieldNum(RetailCommerceValidatorLogCountersByContext, MessageCount), SelectionField::Max);
}

private void setGridFieldsVisibility(
    boolean _products = false,
    boolean _catalog = false,
    boolean _category = false,
    boolean _generic = false,
    boolean _hierarchy = false,
    boolean _attribute = false,
    boolean _attributeGrp = false,
    boolean _channel = false)
{
    ProductGroup.visible(_products);
    CatalogGroup.visible(_catalog);
    CatalogNameGroup.visible(_catalog);
    CategoryGroup.visible(_category);
    GenericGroup.visible(_generic);
    HierarchyGroup.visible(_hierarchy);
    Attribute.visible(_attribute);
    AttributeGroupGrp.visible(_attributeGrp);
    ChannelGroup.visible(_channel);
}

/// <summary>
///  Resets and applies filters
/// </summary>
private void applyResetContextResultRanges(QueryBuildDataSource _qbdsResultsContext)
{
    _qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,ValidationId)).value(SysQuery::value(validationId));
    _qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,BatchJobId)).value(SysQuery::value(currentBatchJob));
    _qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,OMOperatingUnitId)).value(SysQuery::value(0));
    _qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,ProductRefRecId)).value(SysQuery::value(0));
    _qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CategoryRefRecId)).value(SysQuery::value(0));
    _qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CatalogRefRecId)).value(SysQuery::value(0));
    _qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,CategoryHierarchyRefRecId)).value(SysQuery::value(0));
    _qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,AttributeRefRecId)).value(SysQuery::value(0));
    _qbdsResultsContext.addRange(fieldNum(RetailCommerceValidatorLogContext,AttributeGroupRefRecId)).value(SysQuery::value(0));
}

/// <summary>
/// manages the visibility of Entity results group.
/// </summary>
/// <param name = "_sender"> From control</param>
/// <param name = "_e">FormControlEventArgs of the toggle button</param>
private void toggleEntityResultsSectionVisibility(FormControl _sender, FormControlEventArgs _e)
{
    FormButtonControl switchButton = _sender as FormButtonControl;
    //boolean canShowPassedResults = PassedEntities.visible();

    EntityResults.visible(!EntityResults.visible());

    if (EntityResults.visible())
    {
        switchButton.normalImage(ImageReferenceSymbol::CaretLeft);
        switchButton.text("@Retail:HideDetails");
    }
    else
    {
        switchButton.normalImage(ImageReferenceSymbol::CaretRight);
        switchButton.text("@Retail:ShowDetails");
    }

    element.manageResultsGridsVisibility();
    element.manageCountersVisibility();
}

/// <summary>
/// Manages the visibility of passed entity grid.
/// </summary>
/// <param name = "_sender"> From control</param>
/// <param name = "_e">FormControlEventArgs of the toggle button</param>
private void togglePassedEntityGridVisibility(FormControl _sender, FormControlEventArgs _e)
{
    FormButtonControl switchButton = _sender as FormButtonControl;

    ValidatorEntityGrid.visible(!ValidatorEntityGrid.visible());
    PassedEntities.visible(!PassedEntities.visible());

    if (PassedEntities.visible())
    {
        switchButton.normalImage(ImageReferenceSymbol::HideItem);
        switchButton.text("@Retail:RetailCommerceValidatorHidePassedResults");
    }
    else
    {
        switchButton.normalImage(ImageReferenceSymbol::Show);
        switchButton.text("@Retail:RetailCommerceValidatorShowPassedResults");
    }

    element.manageResultsGridsVisibility();
    element.showPassedEntities(PassedEntities.visible());
    element.manageCountersVisibility();
}

private void showPassedEntities(boolean _showPassedEntities)
{
    if (_showPassedEntities)
    {
        RetailCommerceValidatorLogContextPassedResults_DS.queryBuildDataSource().enabled(true);

        switch(EntityType.selection())
        {
            case RetailCommerceValidatorTestStage::Generic:
                element.setPassedEntitiesGridFieldsVisibility(false,false,false,false,false,false,false,true);
                RetailChannelTablePassed_DS.queryBuildDataSource().enabled(true);
                RetailChannelTablePassed_DS.executeQuery();
                break;
            case RetailCommerceValidatorTestStage::Products:
            case RetailCommerceValidatorTestStage::ProductVariants:
            case RetailCommerceValidatorTestStage::ProductKits:
                element.setPassedEntitiesGridFieldsVisibility(true);
                EcoResProductPassed_DS.queryBuildDataSource().enabled(true);
                EcoResProductPassed_DS.executeQuery();
                break;
            case RetailCommerceValidatorTestStage::CommerceCatalogs:
                element.setPassedEntitiesGridFieldsVisibility(false,true);
                CatalogPassed_DS.queryBuildDataSource().enabled(true);
                CatalogPassed_DS.executeQuery();
                break;
            case RetailCommerceValidatorTestStage::CommerceCategories:
                element.setPassedEntitiesGridFieldsVisibility(false,false,true);
                EcoResCategoryPassed_DS.queryBuildDataSource().enabled(true);
                EcoResCategoryPassed_DS.executeQuery();
                break;
            case RetailCommerceValidatorTestStage::CategoryHierarchies:
                element.setPassedEntitiesGridFieldsVisibility(false,false,false,false,true);
                EcoResCategoryHierarchyPassed_DS.queryBuildDataSource().enabled(true);
                EcoResCategoryHierarchyPassed_DS.executeQuery();
                break;
            case RetailCommerceValidatorTestStage::CommerceAttributes:
                element.setPassedEntitiesGridFieldsVisibility(false,false,false,false,false,true);
                EcoResAttributePassed_DS.queryBuildDataSource().enabled(true);
                EcoResAttributePassed_DS.executeQuery();
                break;
            case RetailCommerceValidatorTestStage::CommerceAttributeGroups:
                element.setPassedEntitiesGridFieldsVisibility(false,false,false,false,false,false,true);
                EcoResAttributeGroupPassed_DS.queryBuildDataSource().enabled(true);
                EcoResAttributeGroupPassed_DS.executeQuery();
                break;
            case RetailCommerceValidatorTestStage::CommerceChannels:
                element.setPassedEntitiesGridFieldsVisibility(false,false,false,false,false,false,false,true);
                RetailChannelTablePassed_DS.queryBuildDataSource().enabled(true);
                RetailChannelTablePassed_DS.executeQuery();
                break;
        }

        if (element.canResetRangesIfPassedEntitiesGridIsEmpty(RetailCommerceValidatorLogContextPassedResults_DS.queryBuildDataSource()))
        {
            element.applyResetContextResultRanges(RetailCommerceValidatorLogContextPassedResults_DS.queryBuildDataSource());

            RetailCommerceValidatorLogContextPassedResults_DS.executeQuery();
        }
    }
}

private void setPassedEntitiesGridFieldsVisibility(
    boolean _products = false,
    boolean _catalog = false,
    boolean _category = false,
    boolean _generic = false,
    boolean _hierarchy = false,
    boolean _attribute = false,
    boolean _attributeGrp = false,
    boolean _channel = false)
{
    PassedProductsGrid.visible(_products);
    PassedAttributeGroupsGird.visible(_attributeGrp);
    PassedCategoriesGrid.visible(_category);
    PassedCatalogsGrid.visible(_catalog);
    PassedCategoryHierarchiesGrid.visible(_hierarchy);
    PassedChannelsGrid.visible(_channel);
    PassedAttributesGrid.visible(_attribute);
}

private void manageCountersVisibility()
{
    boolean isFailedResultsVisible = EntityResults.visible();
    boolean isPassedResultsVisible = PassedEntities.isVisible() && LogPassedResultsGrid.isVisible();

    // Failed entity results counter
    Counters.visible(!isFailedResultsVisible);

    // Passed entity results counters
    RetailCommerceValidatorPassedProductCounters_MessageCount.visible(!isPassedResultsVisible);
    RetailCommerceValidatorPassedCatalogCounters_MessageCount.visible(!isPassedResultsVisible);
    RetailCommerceValidatorPassedChannelCounters_MessageCount.visible(!isPassedResultsVisible);
    RetailCommerceValidatorPassedCategoryCounters_MessageCount.visible(!isPassedResultsVisible);
    RetailCommerceValidatorPassedAttributeCounters_MessageCount.visible(!isPassedResultsVisible);
    RetailCommerceValidatorAttibuteGroupCountersPassed_MessageCount.visible(!isPassedResultsVisible);
    RetailCommerceValidatorPassedCategoryHierarchyCounters_MessageCount.visible(!isPassedResultsVisible);
}

private void manageResultsGridsVisibility()
{
    if (EntityResults.visible())
    {
        if (PassedEntities.visible())
        {
            ResultStatus.visible(false);
            QuickFilterResultsPassed.visible(true);
            QuickFilterResults.visible(false);

            LogResultsGrid.visible(false);
            LogPassedResultsGrid.visible(true);
        }
        else if(ValidatorEntityGrid.visible())
        {
            ResultStatus.visible(true);
            QuickFilterResults.visible(true);
            QuickFilterResultsPassed.visible(false);

            LogResultsGrid.visible(true);
            LogPassedResultsGrid.visible(false);
        }
    }
    else
    {
        LogResultsGrid.visible(false);
        LogPassedResultsGrid.visible(false);
    }
}

private boolean canResetRangesIfPassedEntitiesGridIsEmpty(QueryBuildDataSource _qbdsResultsContext)
{
    boolean canResetPassedLogs;

    if (PassedProductsGrid.isVisible() && !EcoResProductPassed_Ds.numberOfRowsLoaded())
    {
        canResetPassedLogs = true;
    }
    else if (PassedAttributeGroupsGird.isVisible() && !EcoResAttributeGroupPassed_DS.numberOfRowsLoaded())
    {
        canResetPassedLogs = true;
    }
    else if (PassedCategoriesGrid.isVisible() && !EcoResCategoryPassed_DS.numberOfRowsLoaded())
    {
        canResetPassedLogs = true;
    }
    else if (PassedCatalogsGrid.isVisible() && !CatalogPassed_DS.numberOfRowsLoaded())
    {
        canResetPassedLogs = true;
    }
    else if (PassedCategoryHierarchiesGrid.isVisible() && !EcoResCategoryHierarchyPassed_DS.numberOfRowsLoaded())
    {
        canResetPassedLogs = true;
    }
    else if (PassedChannelsGrid.isVisible() && !RetailChannelTablePassed_DS.numberOfRowsLoaded())
    {
        canResetPassedLogs = true;
    }
    else if (PassedAttributesGrid.isVisible() && !EcoResAttributePassed_DS.numberOfRowsLoaded())
    {
        canResetPassedLogs = true;
    }

    return canResetPassedLogs;
}

